	var scrollLock = 0;
    var scrolled;
    var loc;

    $(function() {
    	CreativeTemplate.Init();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ChannelBarIsSticky": false,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.Header();
            this.ChannelBar();
            this.Body();
            this.UpcomingEvents();
            this.Footer();
            this.Slideshow();
            this.RsMenu();
            this.Search();

            loc = $("main").offset().top;

            $(window).load(function(){ _this.WindowLoad(); _this.StickyHeader(); });
            $(window).scroll(function(){  _this.StickyHeader(); });
            $(window).resize(function(){ _this.WindowResize(); });

            csGlobalJs.OpenInNewWindowWarning();
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
            this.CalendarListView();
            this.MegaMenu();
            this.JsMediaQueries();

            // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();

            $(".headlines:not(.cs-quyana-mention-app) .ui-articles").each(function() {
                var maxHeight = 0;

                $("li:not(.cs-more-link-parent)", this).each(function() {
                    if($(this).height() > maxHeight) {
                        maxHeight = $(this).outerHeight();
                    }

                    $(".ui-article-title, .ui-article-description", this).wrapAll('
');
                    $(".ui-article", this).css("padding-bottom", $(".ui-article-info", this).height()+23);

                    if($(".ui-article-description", this).text() == "") {
                        $(this).addClass("cs-no-desc");
                    }

                    if(!$(".ui-article-thumb", this).length) {
                        $(this).addClass("cs-no-accent").removeAttr("style");
                        $(".ui-article", this).removeAttr("style");
                        $(".ui-article-description", this).removeAttr("style").show().attr("aria-hidden", "false");
                    } else {
                        $(".ui-article-description", this).attr("aria-hidden", "true");
                    }
                });

                $("li:not(.cs-more-link-parent):not(.cs-no-accent)", this).attr("tabindex", 0).height(maxHeight);
            });

            $(".headlines .ui-articles li:not(.cs-no-accent").bind("mouseenter focus", function() {
            	if($(".ui-article-description", this).text() != "") {
                    var extraHeight = $(this).height() - $(".ui-article-info", this).outerHeight();
                    $(".ui-article-description", this).height(extraHeight).stop().slideDown(500);
                }
                $(".ui-article-description", this).attr("aria-hidden", "false");
            });

            $(".headlines .ui-articles li:not(.cs-no-accent)").bind("mouseleave blur", function() {
            	if($(".ui-article-description", this).text() != "") {
            		$(".ui-article-description", this).stop().slideUp(500, function() { $(".ui-article-description", this).height("auto") });
                }
                $(".ui-article-description", this).attr("aria-hidden", "true");
            });

            $("div.ui-widget.app.navigation li a").each(function() {
            	if(!$("span", this).length) {
            		$(this).wrapInner("");
                }
            });

            this.AppAccordion();

            $(".ui-breadcrumbs li").each(function() {
                if($(this).text() == "" && $(this).index() == 2) {
                	$("a", this).attr("href", window.location.href);
                	$("a span", this).text($(".pagenavigation .ui-widget-header > *").text());
                }
            });

            csGlobalJs.OpenInNewWindowWarning();
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.CalendarListView();
        },

        "StickyHeader": function() {
        	if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var channelBarOffSet = $("header").height();
                        if ($(window).scrollTop() <= channelBarOffSet) {
                            if(this.ChannelBarIsSticky) {
                                $("#gb-page").css("padding", "0");
                                $("header").removeClass("fixed-element");

                                this.ChannelBarIsSticky = false;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                                $(".wcm-controls").css("margin-top", "0px");
                            }
                        } else {
                            if(!this.ChannelBarIsSticky) {
                                $("#gb-page").css("padding", $("header").height()+"px 0 0 0");
                                $("header").hide().addClass("fixed-element");
                                $("header").fadeIn(500);

                                this.ChannelBarIsSticky = true;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("#gb-channel-list-outer").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.ChannelBarIsSticky) {
                            $("#gb-page").css("padding", "0");
                            $("header").removeClass("fixed-element");

                            this.ChannelBarIsSticky = false;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }
                        $("#sw-maincontent").removeAttr("style");
                    break;
                }
            }
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop":
                	$("nav > script").after($(".cs-mystart-dropdown.extra-links"));

                	$(".headlines .ui-article-thumb").removeClass("cs-no-click");
                break;
                case "768":
                    $("#hp-slideshow .rs-gallery-desc-outer").after($("#hp-slideshow .rs-gallery-controls-outer .rs-gallery-control.play-pause"));
                    $("#hp-slideshow .rs-gallery-desc-outer").after($("#hp-slideshow .rs-gallery-controls-outer .rs-gallery-control.next"));
                	$("#hp-slideshow .rs-gallery-desc-outer").after($("#hp-slideshow .rs-gallery-controls-outer .rs-gallery-control.back"));

                	$("nav > script").after($(".cs-mystart-dropdown.extra-links"));

               		$(".headlines .ui-article-thumb").addClass("cs-no-click");

                    $(".headlines .ui-article-thumb").each(function(){
                       	if(!$(this).siblings(".cs-mobile-link").length) {
                        	$(this).after('');
                        }
                    });

