$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=UkSguO0%2fyvHc%2f84dspcSAA%3d%3d&SessionID=899d7029-f146-46ef-a643-e20beaa1dbdd&SiteID=4&ChannelID=1742&SectionID=4922&PageID=15079&HitDate=4%2f22%2f2020+2%3a53%3a47+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.11.210';
   try {
      $.getJSON(beaconURL + '&jsonp=?', function(myData) {});
   } catch(err) { 
      // prevent site error for analytics
   }
