        $('.search-trigger').off('click').click(function(e){
            if( $(this).attr('aria-expanded') == 'false' ){
                $(this)
                    .attr('aria-expanded', 'true')
                    .attr('aria-label', 'Close search input');
                $('#search-container').slideDown();

                setTimeout(function(){
                    $('#swsitesearch-txt-searchstring').focus();
                }, 200);
            }else{
                $(this)
                    .attr('aria-expanded', 'false')
                    .attr('aria-label', 'Open search input');
                $('#search-container').slideUp();
            }
        })

		popularLinks();
		markCustomButtons();

