	function pgScreenSize(){
		var getWidth = $(window).width();

		if(getWidth >= 1024){ //desktop view
			multimediaRotator();
			if("nwabsd" != districtAlias && $("#hp-slideshow #sw-content-container12").length <= 0){
				$('#hp-slideshow .rotator-app-split').append($('#sw-content-container12'));
			}
			spaceHeader();
		}else{ //768 and below
			if( getWidth >= 640 ){ //any bp higher than 480
				multimediaRotator();
			}

			if("nwabsd" != districtAlias && $('#hp-content #sw-content-container12').length <= 0){
				$('#hp-content').prepend($('#sw-content-container12'));
			}
			$('#rs-menu-btn span').addClass("icon-font icon-menu-handle");
		} 
	}

	function multimediaRotator(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
			// MULTIMEDIA ROTATOR
			var galleryW = 1280;
			if("nwabsd" != districtAlias){
				galleryW = 870;
			}
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				"imageWidth"              : galleryW,
				"imageHeight"             : 580,
				"appHeader"               : "no",
				"titles"                  : "yes",
				"captions"                : "yes",
				"galleryOverlay"	  : "no",
				"linkedElement"		  : ["title"], // ["image", "title", "overlay", "all"]
				"playPause"               : "yes",
				"mainControls"            : "yes",
				"contMoreLink"            : "no",
				"contMoreLinkText"        : "Read More",
				"descMoreLink"            : "no",
				"descMoreLinkText"        : "Read More",
				"bullets"                 : "no",
				"bulletControls"          : "no",
		 		"thumbnails"              : "no",
				"thumbnailWidth"          : 122,
				"thumbnailHeight"         : 53,
				"thumbnailSlideDirection" : "horizontal", // horizontal or vertical
				"hoverPause"              : "yes",
		                "fullScreenRotator"       : "no", // image dimensions work well at 1500x915
				"fullScreenVideo"         : "no",
				"transitionType"          : "fade", // fade or slide
				"transitionSpeed"         : 3,
				"transitionDelay"         : 8,
				"onImageLoad"             : function() { },
				"allImagesLoaded"         : function() { },
				"onThumbnailLoad"         : function() { },
				"allThumbnailsLoaded"     : function() { },
				"onTransitionStart"       : function() { },
				"onTransitionEnd"         : function() {
					if( $('.rs-title.next h1 a').length > 0 ){
						$('.rs-title.next h1 a').append('Read More');
					}
				},
				"watchVidClick"           : function() { },
				"closeVidClick"           : function() { },
				"allLoaded"               : function() { 
					if( $('#hp-slideshow .rs-gallery-control').length > 0 ){
						$('#hp-slideshow .rs-gallery-control').addClass('icon-font');
					}

					$('#hp-slideshow .rs-gallery-desc').append('
');

					if( $('.rs-title.active h1 a').length > 0 ){
						$('.rs-title.active h1 a').append('Read More');
					}

					if("nwabsd" != districtAlias){
						$("#hp-slideshow").addClass("subsite-rotator");
