	function searchText() {
		//DOC custom mystart search placeholder
        //SEARCH TEXT
        var defSearchText = "SEARCH";

        var searchBox = $('#swsitesearch-txt-searchstring');

        searchBox
            .attr("aria-label", "Search input")
            .attr("placeholder", '')
            .val(defSearchText)
            .focus(function() {
                if($(this).val() == defSearchText) {
                    $(this).val("");
                }
            })
            .blur(function() {
                if($(this).val() == "") {
                    $(this).val(defSearchText);
                }
            })
            .on('keydown', function(e) { // Trigger the click event from the keyboard
                var code = e.which;
                // 13 = Return, 32 = Space
                if ((code === 9) ) {
                    $('.search-trigger').click();
                }
            });
	}

