	function popularLinks(){
		$('.pop-trigger').click(function(e){
			var popOuter = $('#gb-popular-links-outer');
			if(popOuter.hasClass('open-menu')){
				popOuter
                	.removeClass('open-menu')
                    .attr('aria-hidden', 'true');  

                $(this).attr('aria-expanded', 'false');
				$(' > span.icon-font', this).addClass('icon-arrow-left').removeClass('icon-arrow-up');
			}else{
				popOuter
                	.addClass('open-menu')
                    .attr('aria-hidden', 'false');

                setTimeout(function(){
                	$('.global-icon.first', popOuter).attr('tabindex', 0).focus();
				}, 300);

                $(this).attr('aria-expanded', 'true');
				$(' > span.icon-font', this).addClass('icon-arrow-up').removeClass('icon-arrow-left');
			}
			popOuter.slideToggle();
		});

		$("#gb-popular-links ul").creativeIcons({
		        'iconNum'      : '7', // NUMBER OF ICONS
		        'iconColor'    : 'rgba(255, 255, 255, 0.7)', // ICON COLOR
		        'icon1'        : ['People/3.png', 'Kronos', 'https://secure.saashr.com/ta/6093510.clock', '_blank'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		        'icon2'        : ['Communication/16.png', 'District Email', 'https://owa.nwarctic.org', '_blank'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		        'icon3'        : ['/cms/lib/AK01001584/Centricity/Template/GlobalAssets/icons/canvaslogo greenish.png', 'Canvas', 'https://nwarctic.instructure.com/login/ldap', '_blank'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		        'icon4'        : ['Classroom/8.png', 'OverDrive', 'https://alaskaschoolsak.libraryreserve.com/10/45/en/SignIn.htm?url=Default.htm', '_blank'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		        'icon5'        : ['Student Program/3.png', 'Revtrak', 'http://nwarctic.revtrak.net/', '_blank'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		        'icon6'        : ['Social Media/5.png', 'Microsoft Portal', 'https://portal.office.com', '_blank'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		        'icon7'        : ['/cms/lib/AK01001584/Centricity/Template/GlobalAssets/icons/ivisions.png', 'iVisions', 'https://ivisions.nwarctic.org/ess', '_blank'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
		        'siteID'       : '4',
		        'siteAlias'    : 'nwabsd',
		        'calendarLink' : '/Page/2',
		        'contactEmail' : 'techhelp@nwarctic.org',
		        'onComplete'   : function(){ 
		        	$("> a", this).wrap("
");
		        	$("> li", this).after("\n"); //fixes the justify css trick issue
		        	var activeCount = 0;
		        	$('#gb-popular-links ul > li').each(function(k,v){
		        		if( $('> a', this).attr('href') == '' ){
		        			$(this).hide();
		        		}else{
		        			activeCount++;
		        		}
		        	});
		        	if( activeCount < 5 ){ //if there are less than 5 showing links center them dont use the justify trick
		        		$('#gb-popular-links > ul').css('text-align', 'center');
		        	}

                    $('#gb-popular-links a').attr('tabindex', -1);
		        }
		});

        $('#gb-popular-links a').keydown(function(e){
            var keycode = (e.keyCode ? e.keyCode : e.which);
            var linkObj = $('#gb-popular-links a');

            if(keycode == '9') {
                e.preventDefault();
                $('.pop-trigger').focus();
            }else{
                switch(keycode){
                    case 37: //up/left
                    case 38:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS FIRST ITEM
                        if(linkObj.index($(this)) == 0) {
                            $('.pop-trigger').focus();
                        } else {
                            linkObj[linkObj.index($(this)) - 1].focus();
                        }
                        break;
                    case 39:
                    case 40: //down/right
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if(linkObj.index($(this)) == (linkObj.length - 1) ) {
                            // FOCUS FIRST ITEM
                            linkObj[0].focus();
                        } else {
                            // FOCUS NEXT ITEM
                            linkObj[linkObj.index($(this)) + 1].focus();
                        }
                        break;
                    default: break;
                }
            }
        });
	}

	function markCustomButtons(){
		var btns = [
			"#gb-footer-links > a",
			".more-link",
			".view-calendar-link"
		];

		$.each(btns, function(k, v){
			$(v).addClass('custom-button');

			if( v == ".more-link" || v == ".view-calendar-link" ){
				$(v).addClass('custom-more');
			}
		});	
	}

	/*
	** smoothScroll
	** Use: This will smoothly scroll to the id that was passed to the function.
	** Return: N/A
	** Parameter(s): to (jQuery "#id") The id of the section to be scolled to.
	** 		offset (int) The amount of space you want to be left on the top of the element after scroll (in a whole number int value)
	*/
	function smoothScroll( to, offset ){
	    if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
	    var target = $(to);

	    if (target.length) {
	        $('html,body').animate({
	            scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
	        }, 800, function(){
	            $("html, body").stop(true, false);
	        });
	        return false;
	    }
	}

