$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=L69%2f8XNroAFjHZe87UFdvA%3d%3d&SessionID=7eb592ed-97d0-48b6-a13b-0f07afcac3f5&SiteID=4&ChannelID=166&SectionID=1811&PageID=6641&HitDate=4%2f22%2f2020+3%3a54%3a58+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.31.169';
