function schoolMenu(){
        $(".sw-mystart-button, .sw-mystart-dropdown").attr("tabindex", 0);
        $(".sw-dropdown-list li").attr("tabindex", 0);
        $(".sw-mystart-dropdown").focus(function() {
                $(".sw-dropdown").css("display", "block");
        }); 
};
