        "StreamingVideo": function() {
        	// FOR SCOPE
            var _this = this;

        	var headerHeight = $("header").outerHeight();
            var gbIconsHeight = $("#gb-global-icons-outer").outerHeight();
            var mystartrHeight = $("#gb-mystart-outer").outerHeight();
            var subHeights = headerHeight + gbIconsHeight + mystartrHeight;
            var myVideo = document.getElementById("cs-custom-video");
            var windowWidth = $(window).width();
            var windowHeight = $(window).height();
            windowHeight = windowHeight - subHeights;
            myVideo.width = windowWidth;

            //Resize video
            _this.CustomVideoResize();
            $(window).bind("resize", function(){ 
                _this.CustomVideoResize();
            });

            // PAUSE PLAY BUTTONS
            $(".cs-video-btn.play-btn").addClass("active");
            $("#hp-video-outer").on("click keydown", ".cs-video-btn.play-btn", function(e) {
                if(_this.A11yClick(e)) {
                	e.preventDefault();
                	if($(this).hasClass("active")) {
                    	//do nothing
                    } else {
                      $(".cs-video-btn").removeClass("active");
                      $(this).addClass("active");
                      myVideo.play();
                    }
                }
            });
            $("#hp-video-outer").on("click keydown", ".cs-video-btn.pause-btn", function(e) {
                if(_this.A11yClick(e)) {
                	e.preventDefault();
                	if($(this).hasClass("active")) {
                    	//do nothing
                    } else {
                      $(".cs-video-btn").removeClass("active");
                      $(this).addClass("active");
                      myVideo.pause();
                    }
                }
            });
        },

        "StaticImage": function() {
        	var _this = this;

            $("#hp-video-outer").addClass("static-image");
            $("").prependTo("#hp-video-outer");
            $("#hp-video-outer #cs-custom-video").remove();

            //RESIZE CONTAINER
            var headerHeight = $("header").outerHeight();
            var gbIconsHeight = $("#gb-global-icons-outer").outerHeight();
            var mystartrHeight = $("#gb-mystart-outer").outerHeight();
            var subHeights = headerHeight + gbIconsHeight + mystartrHeight;
            var screenWidth = $(window).width();
            var screenHeight = $(window).height();
            var screenRatio = screenHeight / screenWidth;
            var containerHeight = screenWidth * screenRatio;
            screenHeight = screenHeight - subHeights;

            if($(window).width() > 1023) {
                $("#hp-video-outer").css({"height": screenHeight + "px"});
            } else {
                $("#hp-video-outer").css({"height": containerHeight + "px"});
            }

            $(window).bind("resize", function(){ 
                var headerHeight = $("header").outerHeight();
                var gbIconsHeight = $("#gb-global-icons-outer").outerHeight();
                var mystartrHeight = $("#gb-mystart-outer").outerHeight();
                var subHeights = headerHeight + gbIconsHeight + mystartrHeight;
                var screenWidth = $(window).width();
                var screenHeight = $(window).height();
                var screenRatio = screenHeight / screenWidth;
                var containerHeight = screenWidth * screenRatio;
                screenHeight = screenHeight - subHeights;

                if($(window).width() > 1023) {
                    $("#hp-video-outer").css({"height": screenHeight + "px"});
                } else {
                    $("#hp-video-outer").css({"height": containerHeight + "px"});
                }
            });
        },

        "CustomVideoResize": function() {
        	var getWidth = $(window).width();
            var headerHeight = $("header").outerHeight();
            var gbIconsHeight = $("#gb-global-icons-outer").outerHeight();
            var mystartrHeight = $("#gb-mystart-outer").outerHeight();
            var subHeights = headerHeight + gbIconsHeight + mystartrHeight;
            var myVideo = document.getElementById("cs-custom-video");
            if (getWidth < 1024) {
                myVideo.play();
            }
            var topOffSet = $("#hp-video-outer").offset().top;
            var screenWidth = $(window).width();
            var screenHeight = $(window).height();
            var screenRatio = screenHeight / screenWidth;
            var containerHeight = screenWidth * screenRatio;
            var vidWidth = screenWidth, vidHeight = 0;

            screenHeight = screenHeight - subHeights;

            // GET IMAGE RATIO
            var initW = 853.5, initH = 480;
            var ratio = initH / initW;
            vidHeight = vidWidth * ratio;
            if(vidHeight < screenHeight){
                vidHeight = screenHeight;
                vidWidth = vidHeight / ratio;
            }
            var centerWidth = (vidWidth - screenWidth) / 2;
            var centerHeight = (vidHeight - screenHeight) / 2;



            // APPLY THE DIMENSIONS
            if($(window).width() > 1023) {
                $("#hp-video-outer").css({"height": screenHeight + "px"});
            } else {
                $("#hp-video-outer").css({"height": containerHeight + "px"});
            }

            $("#cs-custom-video").css({"margin": "-" + centerHeight + "px 0px 0px 0px"});
            $("#cs-custom-video").css({"width": vidWidth + "px"});
        },

