    function LoadMinibaseList135756(PageIndex){
        var FilterFields = "";
        if ($('#sw-content-minibaselist135756-filterquery').length > 0){
        	FilterFields =encodeURIComponent($('#sw-content-minibaselist135756-filterquery').val());
        }else{
            FilterFields = escape(CreateFilterQuery135756());
        }
        var DisplayType = $("#sw-content-minibaselist135756-displaytype").val();
        if (PageIndex === undefined) {
            PageIndex = $("#sw-content-minibaselist135756-pageindex").val();
        }
        var success = '$("#sw-content-minibaselist135756-pageindex").val(' + PageIndex + ');';
        
        if (0 == 1)
        { window.open( "https://www.cusd80.com//site/Default.aspx?DomainID=0&PageID=0&PageType=22&ModuleInstanceID=131746&PageModuleInstanceID=135756&FilterFields=" + FilterFields + "&DirectoryType=" + DisplayType + "&PageIndex=" + PageIndex);
        }
        else 
        { GetContent("https://www.cusd80.com//site/UserControls/Minibase/MinibaseListWrapper.aspx?ModuleInstanceID=131746&PageModuleInstanceID=135756&FilterFields=" + FilterFields + "&DirectoryType=" + DisplayType + "&PageIndex=" + PageIndex, "sw-content-minibaselist135756", 2, success);
        }
    
    }
    function LoadMinibaseFilter135756(){
        GetContent("https://www.cusd80.com//site/UserControls/Minibase/MinibaseFilterWrapper.aspx?ModuleInstanceID=131746&PageModuleInstanceID=135756", "sw-content-minibaselist135756", 2);
    }
    function CreateFilterQuery135756(){
        var filterquery = "";
        $.each($('#sw-content-minibasefilter135756 label'), function(i){
            var id = $(this).attr("for");   
            if($('#' + id).val() != ''){
                var fieldindex = id.substring(id.lastIndexOf('-') + 1);
                //filterquery += $(this).text() 
                
                var comparetype = "E";
                if ($('#sw-content-minibasefilter-field135756' + fieldindex + '-comparetype').val() !== undefined) {
                    comparetype = $('#sw-content-minibasefilter-field135756' + fieldindex + '-comparetype').val();
                }
                filterquery += fieldindex + ":" + comparetype + ":";
                if (comparetype != "E") { 
                    filterquery += $('#' + id).val().replace(/%/g, '[%]').replace(/:/g, '[$C0l0n$]').replace(/;/g, '[$Semic0l0n$]') + ";";
                } else {
                    filterquery += $('#' + id).val().replace(/:/g, '[$C0l0n$]').replace(/;/g, '[$Semic0l0n$]') + ";";
                }
            }            
            
            /*if(i < ($('#sw-content-minibasefilter131746 label').length - 1)) {
                filterquery += ";";
            }*/
        });
        filterquery += "";
        return filterquery;
    }

    //ADA Paging
    $(document).ready(function () {
        $('a li.ui-page-number').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('li.ui-page-number a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('#ui-paging-next-container a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('#ui-paging-prev-container a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('#ui-paging-first-container a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('#ui-paging-last-container a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        var pageIndParamPos = 13;

        if ($('.ui-page-number-current a').html() == '1') {
            if (typeof viewLoc == 'undefined') {
                if (window.matchMedia('(max-width: 1023px)').matches && window.matchMedia('(min-width: 640px)').matches) {
                    hideLarge();
                } else if (window.matchMedia('(max-width: 639px)').matches) {
                    hideMedAndLarge();
                }
            } else if (viewLoc.indexOf('section') == -1) {
                pageIndParamPos = 13;
                var pwidth = $('#ui-paging-container').parent().width();
                if (pwidth > 415 && pwidth < 475) {
                    hideLarge();
                } else if (pwidth < 415) {
                    hideMedAndLarge();
                }
            }
        }

        function hideLarge() {
            $('.ui-large-page').hide();

            $('.ui-next-group a').each(function () {
                var tooLong = $(this).attr('onclick').split(',');

                tooLong[pageIndParamPos] = 9;
                tooLong.join(',');

                $(this).attr('onclick', tooLong);
            });
        }

        function hideMedAndLarge() {
            $('.ui-large-page').hide();
            $('.ui-medium-page').hide();

            $('.ui-next-group a').each(function () {
                var tooLong = $(this).attr('onclick').split(',');

                tooLong[pageIndParamPos] = 4;
                tooLong.join(',');

                $(this).attr('onclick', tooLong);
            });
        }
        
    });

