        var pageIndParamPos = 13;

        if ($('.ui-page-number-current a').html() == '1') {
            if (typeof viewLoc == 'undefined') {
                if (window.matchMedia('(max-width: 1023px)').matches && window.matchMedia('(min-width: 640px)').matches) {
                    hideLarge();
                } else if (window.matchMedia('(max-width: 639px)').matches) {
                    hideMedAndLarge();
                }
            } else if (viewLoc.indexOf('section') == -1) {
                pageIndParamPos = 13;
                var pwidth = $('#ui-paging-container').parent().width();
                if (pwidth > 415 && pwidth < 475) {
                    hideLarge();
                } else if (pwidth < 415) {
                    hideMedAndLarge();
                }
            }
        }

        function hideLarge() {
            $('.ui-large-page').hide();

            $('.ui-next-group a').each(function () {
                var tooLong = $(this).attr('onclick').split(',');

                tooLong[pageIndParamPos] = 9;
                tooLong.join(',');

                $(this).attr('onclick', tooLong);
            });
        }

        function hideMedAndLarge() {
            $('.ui-large-page').hide();
            $('.ui-medium-page').hide();

            $('.ui-next-group a').each(function () {
                var tooLong = $(this).attr('onclick').split(',');

                tooLong[pageIndParamPos] = 4;
                tooLong.join(',');

                $(this).attr('onclick', tooLong);
            });
        }
        
    });

$(document).ready(function (){
$(".tag-list li a").keypress(function(e) { if(e.which == 13) { $(this).click();   }});
});
function LoadGroupedData(container, MIID, PMI, groupYear, groupMonth, groupBy, tag) {
  //ViewToUse looks at the hidden Sidebar List View defined in the Builder View.
  var viewToUse = "";
    if($('#hid-'+MIID+'-SidebarListView').length > 0){
      viewToUse = $("#hid-" + MIID + "-SidebarListView").val();
    };
  GetContent('https://www.cusd80.com//cms/UserControls/ModuleView/ModuleViewRendererWrapper.aspx?DomainID=11232&PageID=106881&ModuleInstanceID=' + MIID + '&PageModuleInstanceID=' + PMI + '&RenderLoc=0&FromRenderLoc=0&GroupYear=' + groupYear + '&GroupMonth=' + groupMonth + '&GroupByField=' + groupBy + '&EnableQuirksMode=0&ViewID=' + viewToUse + '&Tag=' + tag, container, 2, 'chkSidebar();');
}
function LoadData(container, MIID, PMI, flexDataID, groupYear, groupMonth, groupBy, targetView, tag) {
  if(targetView !== undefined || targetView.Length() == 0){
  //targetView looks at the hidden Detail View defined in the Builder View.
      targetView = $('#hid-'+MIID+'-DetailView').val();
   }
  GetContent('https://www.cusd80.com//cms/UserControls/ModuleView/ModuleViewRendererWrapper.aspx?DomainID=11232&PageID=106881&ModuleInstanceID=' + MIID + '&PageModuleInstanceID=' + PMI + '&FlexDataID=' + flexDataID + '&GroupYear=' + groupYear + '&GroupMonth=' + groupMonth + '&GroupByField=' + groupBy + '&RenderLoc=0&FromRenderLoc=0&EnableQuirksMode=0&Tag=' + tag + '&ViewID=' + targetView, container, 2, 'chkSidebar();');
}
function LoadTaggedData(container, MIID, PMI, tag) {
  //ViewToUse looks at the hidden Sidebar List View defined in the Builder View.
  var viewToUse = "";
    if($('#hid-'+MIID+'-SidebarListView').length > 0){
      viewToUse = $("#hid-" + MIID + "-SidebarListView").val();
    };
  GetContent('https://www.cusd80.com//cms/UserControls/ModuleView/ModuleViewRendererWrapper.aspx?DomainID=11232&PageID=106881&ModuleInstanceID=' + MIID + '&PageModuleInstanceID=' + PMI + '&RenderLoc=0&FromRenderLoc=0&Tag=' + tag + '&EnableQuirksMode=0&ViewID='+viewToUse, container, 2, 'chkSidebar();');
  setTimeout(function(){ $("#module-content-"+ MIID +"").find('[tabindex]:first').focus(); }, 200);
}

