input.script:
    function Reply(name, id) 
    {
        jQuery("#dnn_ctr825_View_Index_ctl00_hfReply_UserName").val(name);
        jQuery("#dnn_ctr825_View_Index_ctl00_hfReply_ID").val(id);

        var box = jQuery("#CtlTextArea825");
        box.focus();
        box.val("@" + name + ": " + box.val());
    }

