	var ConnectDiv = '

';
	var ProductType = '1';
	var IsCommonLogin = 1;

link.script:
    $(document).ready(function () {
	
        var retryCount = 0;
		$("body").prepend(ConnectDiv);	

		function initializeToolbar() {
		    if (++retryCount <= 3) {
		        $.ajax({
		            url: ConnectToolbar,
		            success: function (data) {
		                $("#ConnectSuite").html(data);
		                ConnectToolbarRequest();
						//ShowLoginInfo();
							
						//$("#divLogin").remove();
						//$("#divSignIN").remove();
		            },
		            error: function (err) {
		                if (console && typeof console.log == "function") {
		                    console.log("Toolbar failed to load, retrying", err);
		                }
		                window.setTimeout(initializeToolbar, 300);
		            }
		        });
		    }
		}
		initializeToolbar();
	});
	
	function  firstLogin()
	{
	  if(ProductType == 4){
		window.parent.location.href = ClientURL.replace('/#', '') + '/?TokenID=' + TokenID;
	  }
	  else{
		if(ProductType == 5)
			window.parent.location.href = ClientURL.replace('/#', '') + '/Default.aspx?TokenID=' + TokenID + '&UniqueId=' + UniqueID + '&isredirect';
		else
			window.parent.location.href = ClientURL.replace('/#', '') + '/' + 'admin1' + '/finishlogin.asp?TokenID=' + TokenID;
	  }
	}
	
