        "HpEvents": function() {
        	var template = this;

            $(".hp-content.one .ui-widget.app.upcomingevents").each(function() {
            	if($(this).closest("div[id*='pmi-']").next("div[id*='pmi-']").length) {
                	$(this).addClass("pad-bottom");
                }

            	var thisApp = this;
                $(".ui-articles", this).removeAttr("style");
                $(".ui-articles > li", this).removeAttr("aria-hidden");
                $(".ui-articles > li a", this).removeAttr("tabindex");
                $(".ui-articles > li .upcoming-column.right", this).removeAttr("aria-hidden");

                var eventsPerRow = 0;
                var eventsProps = {
                    "eventNum": $(".ui-articles > li", this).length,
                    "eventTotalHeight": $(".ui-articles", this).outerHeight(true),
                    "eventPanelHeight": $(".ui-articles > li:first-child", this).outerHeight(true) + $(".ui-articles > li:first-child .upcoming-column.left .sw-calendar-block-date", this).outerHeight(true), // HEIGHT OF FIRST ARTICLE + HEIGHT OF MONTH/DAY BLOCK
                    "desktop": {
                        "eventsPerRow": 4,
                        "numOfRowsToShow": 2
                    },
                    "sevenSixtyEight": {
                        "eventsPerRow": 2,
                        "numOfRowsToShow": 2
                    },
                    "sixForty": {
                        "eventsPerRow": 2,
                        "numOfRowsToShow": 2
                    }
                }

                switch(template.GetBreakPoint()) {
                    case "desktop":
                        eventsPerRow = eventsProps.desktop.eventsPerRow;
                    break;
                    case "768":
                        eventsPerRow = eventsProps.sevenSixtyEight.eventsPerRow;
                    break;
                    case "640":
                        eventsPerRow = eventsProps.sixForty.eventsPerRow;
                    break;
                }

                if(eventsProps.eventNum > eventsPerRow) {
                    $(".cs-events-toggle", this).attr("aria-label", "Expand Events").text("Expand Events").addClass("collapsed").removeClass("expanded");

                    $(".ui-articles > li:nth-child(n+" + ((eventsPerRow * 2) + 1) + ")", this).attr("aria-hidden", "true");
                	$(".ui-articles > li:nth-child(n+" + (eventsPerRow + 1) + ") a", this).attr("tabindex", "-1");
                	$(".ui-articles > li:nth-child(n+" + (eventsPerRow + 1) + "):nth-child(-n" + (eventsPerRow * 2) + ") .upcoming-column.right", this).attr("aria-hidden", "true");

                    $(this).addClass("expandable");
                    $(".ui-articles", this).height(eventsProps.eventPanelHeight);
                } else {
                	$(".ui-articles > li", this).removeAttr("aria-hidden");
                	$(".ui-articles > li a", this).removeAttr("tabindex");
                	$(".ui-articles > li .upcoming-column.right", this).removeAttr("aria-hidden");

                    $(this).removeClass("expandable");
                    $(".ui-articles", this).css("height", "auto");
                }

                $(this).off().on("click keydown", ".cs-events-toggle", function(e) {
                    if(template.AllyClick(e)) {
                        e.preventDefault();

                        // EXPAND EVENTS
                        if($(this).hasClass("collapsed")) {
                            $(this).attr("aria-label", "Collapse Events").text("Collapse Events").addClass("expanded").removeClass("collapsed");

                            $(".ui-articles > li", thisApp).removeAttr("aria-hidden");
                			$(".ui-articles > li a", thisApp).removeAttr("tabindex");
                            $(".ui-articles > li .upcoming-column.right[aria-hidden='true']:first", thisApp).find("a:first").focus();
                			$(".ui-articles > li .upcoming-column.right", thisApp).removeAttr("aria-hidden");

                            $(".ui-articles", thisApp).animate({
                                height: eventsProps.eventTotalHeight
                            }, 500);
                        }

                        // COLLAPSE EVENTS
                        else {
                            $(this).attr("aria-label", "Expand Events").text("Expand Events").addClass("collapsed").removeClass("expanded");

                            $(".ui-articles > li:nth-child(n+" + ((eventsPerRow * 2) + 1) + ")", thisApp).attr("aria-hidden", "true");
                			$(".ui-articles > li:nth-child(n+" + (eventsPerRow + 1) + ") a", thisApp).attr("tabindex", "-1");
                			$(".ui-articles > li:nth-child(n+" + (eventsPerRow + 1) + "):nth-child(-n" + (eventsPerRow * 2) + ") .upcoming-column.right", thisApp).attr("aria-hidden", "true");

                            $(".ui-articles", thisApp).animate({
                                height: eventsProps.eventPanelHeight
                            }, 500);
                        }
                    }
                });
            });
        },

