                        function videoDimReset() {
                            if($(".rs-gallery-videos").length) {
                                if(_this.GetBreakPoint() == "desktop") {
                                    $(".rs-gallery-videos .video iframe", element).css({"width": "auto", "height": "auto"});
                                    var screenWidth = $(window).width();
                                    var screenHeight = $(window).height();
                                    var initVidW = 800, initVidH = 600;
                                    var vidRatio = initVidH / initVidW;
                                    var vidWidth = $("#rs-gallery-videos-" + elemID + " .video").width();
                                    var vidHeight = vidWidth * vidRatio;

                                    if((vidHeight + 100) > screenHeight){
                                        vidHeight = screenHeight - 140;
                                    }

                                    $("#rs-gallery-videos-" + elemID + " .video").css({"height": (vidHeight + 40)});
                                    $("#rs-gallery-videos-" + elemID + " .video iframe").css({"width": vidWidth, "height": vidHeight});
                                } else {
                                	$(".rs-gallery-videos .video iframe", element).css({"width": "auto", "height": "auto"});
                                    var vidWidth = $(".rs-gallery-videos .video", element).width();
                                    var vidHeight = $(".rs-gallery-videos .video", element).height() - 27;
                                    $(".rs-gallery-videos .video iframe", element).css({"width": vidWidth, "height": vidHeight});
                                }
                            }
                        }

                        var initW = 1600, initH = 680;
						var ratio = initH / initW;
                        var screenWidth = $(window).width();

                        if(_this.GetBreakPoint() == "desktop") {
                        	var topOffSet = $(element).offset().top;
							var screenHeight = $(window).height() - topOffSet;
							var screenRatio = screenHeight / screenWidth;
							var containerHeight = screenWidth * screenRatio;
							var imgWidth = screenWidth, imgHeight = 0;

							if($(".rs-gallery-videos").length) {
								var vidWidth = $(".rs-gallery-videos .video", element).width();
								var vidHeight = $(".rs-gallery-videos .video", element).height() - 27;
							}

							var imgHeight = imgWidth * ratio;

							if(imgHeight < screenHeight){
								imgHeight = screenHeight;
								imgWidth = imgHeight / ratio;
							}

							var centerWidth = (imgWidth - screenWidth) / 2;
							var centerHeight = (imgHeight - screenHeight) / 2;

							$(".rs-gallery-container", element).css("height", containerHeight);
							$(".rs-gallery-container .rs-gallery-images", element).css("height", containerHeight);
							$(".rs-gallery-container .rs-gallery-images .rs-gallery-images-list", element).css("height", containerHeight);
							$(".rs-gallery-container .rs-gallery-images .rs-gallery-images-list li", element).css("height", containerHeight);
							$(".rs-gallery-container .rs-gallery-images .rs-gallery-images-list li img", element).css({"width": imgWidth + "px", "height": imgHeight + "px", "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});
                        } else {
                        	var imgHeight = $(window).width() * .610381; // RATIO FROM DESIGN - 470 / 770

							if($(".rs-gallery-videos").length) {
								var vidWidth = $(".rs-gallery-videos .video", element).width();
								var vidHeight = $(".rs-gallery-videos .video", element).height() - 27;
							}

							var imgWidth = imgHeight / ratio;
                            var centerWidth = (imgWidth - screenWidth) / 2;

							$(".rs-gallery-container", element).removeAttr("style");
							$(".rs-gallery-container .rs-gallery-images", element).css("height", "auto");
							$(".rs-gallery-container .rs-gallery-images .rs-gallery-images-list", element).removeAttr("style");
							$(".rs-gallery-container .rs-gallery-images .rs-gallery-images-list li", element).removeAttr("style");
							$(".rs-gallery-container .rs-gallery-images .rs-gallery-images-list li img", element).css({"width": imgWidth + "px", "height": "100%", "margin": "0px 0px 0px -" + centerWidth + "px"});
                        }

                        videoDimReset();
                    },
