        "Search": function() {
        	// FOR SCOPE
            var _this = this;

            $("#gb-search-button").click(function(e){
                e.preventDefault();

                if($.trim($("#gb-search-input").val()) != "Search" && $.trim($("#gb-search-input").val()) != "") {
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                }
            });

            $("#gb-search-input").focus(function() {
                if($(this).val() == "Search") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search");
                }
            }).keydown(function(e) {
            	if(e.keyCode == _this.KeyCodes.enter) {
                	e.preventDefault();

                    if($.trim($("#gb-search-input").val()) != "Search" && $.trim($("#gb-search-input").val()) != "") {
                    	window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                    }
                }
            });

            $("#gb-search-form").focusin(function() {
            	$(this).addClass("focus");
            }).focusout(function() {
            	$(this).removeClass("focus");
            });
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

