        "CanvasShape": function(canvas, shape, width, height, extraProps){

        	switch(shape){
            	case "header-double-triangles":
                	var angleOffset = 5;
                	canvas.beginPath();
                    canvas.moveTo(0, 0);
                    canvas.lineTo(width - angleOffset, 0);
                    canvas.lineTo(0, height);
                    canvas.lineTo(0, 0);
                    canvas.fillStyle = extraProps.prop1;
                    canvas.fill();
                    canvas.closePath();

                    canvas.beginPath();
                    canvas.moveTo(0, 0);
                    canvas.lineTo(width - angleOffset, 0);
                    canvas.lineTo((width - angleOffset)/2, height/2);
                    canvas.lineTo(0, 0);
                    canvas.fillStyle = extraProps.prop2;
                    canvas.fill();
                    canvas.closePath();

                    canvas.beginPath();
                    canvas.moveTo(width - angleOffset, 0);
                    canvas.lineTo(width, 0);
                    canvas.lineTo(angleOffset, height);
                    canvas.lineTo(0, height);
                    canvas.lineTo(width - angleOffset, 0);
                    canvas.fillStyle = extraProps.prop3;
                    canvas.fill();
                    canvas.closePath();
                	break;

                case "double-right-angle-triangles":
                	canvas.beginPath();
                    canvas.moveTo(0, 0);
                    canvas.lineTo(width - 8, 0);
                    canvas.lineTo(0, height);
                    canvas.lineTo(0, 0);
                    canvas.fillStyle = extraProps.prop1;
                    canvas.fill();
                    canvas.closePath();

                    canvas.beginPath();
                    canvas.moveTo(width, 0);
                    canvas.lineTo(width, height);
                    canvas.lineTo(8, height);
                    canvas.lineTo(width, 0);
                    canvas.fillStyle = extraProps.prop2;
                    canvas.fill();
                    canvas.closePath();
                	break;

                case "right-angle-triangle-left":
                	canvas.beginPath();
                    canvas.moveTo(0, 0);
                    canvas.lineTo(width, 0);
                    canvas.lineTo(0, height);
                    canvas.lineTo(0, 0);
                	break;

                case "right-angle-triangle-right":
                	canvas.beginPath();
                    canvas.moveTo(width, 0);
                    canvas.lineTo(width, height);
                    canvas.lineTo(0, height);
                    canvas.lineTo(width, 0);
                	break;

                case "isosceles-triangle":
                	canvas.beginPath();
                    canvas.moveTo(0, 0);
                    canvas.lineTo(width, 0);
                    canvas.lineTo(width/2, height);
                    canvas.lineTo(0, 0);
                	break;

                default:
                	break;
            }

        },


