    function OpenCategoryFilter() {
        OpenDialogOverlay("WindowLargeModal", { LoadType: "U", LoadURL: "https://www.cajonvalley.net//site/UserControls/Calendar/CategoryFilterNewWrapper.aspx?DomainID=4&ModuleInstanceID=10913" });
