    $("a.Opener").click(function (event) {
        event.preventDefault();
        var arrow = $(this).find("+ .slicknav_arrow");
        if (arrow.length > 0) {
            $(arrow).click();
        }
    });
    $(document).ready(function () {
        var obj = $(".current_page");
        var searching = true;
        if (obj.length > 0) {
            while (searching) {
                obj = $(obj).parent();
                var arrow = $(obj).find("> a > .slicknav_arrow");
                if (arrow.length > 0) {
                    $(arrow).click();
                }
                if ($(obj).attr("class") == "slicknav_menu") {
                    searching = false;
                }
            }
        }
    });
