                                '
' + buildSchoolLists("Elementary") + '' +
                                '
' + buildSchoolLists("Middle") + '' +
                                '
' + buildSchoolLists("High") + '' +
                                '
' + buildSchoolLists("Alternative") + '' +
                                '
' + buildSchoolLists("AdultPrograms") + '' +
                                '
' + buildSchoolLists("AfterSchoolPrograms") + '' +
                            '' +
                            '
