                        channelBarData[loopIndex].channel.text = $("> a span", this).text();
                        channelBarData[loopIndex].channel.url = $("> a", this).attr("href");
                        channelBarData[loopIndex].channel.target = ($("> a", this).attr("target") == undefined) ? "_self" : $("> a", this).attr("target");

                        // LOOP THROUGH SECTION LIST
                        $(".sw-channel-dropdown li", this).each(function() {
                            // GATHER SECTION ATTRIBUTES
                            channelBarData[loopIndex].sections.push({
                                "id": (!$(this).hasClass("sw-channel-more-li")) ? $(this).attr("id") : "more-link",
                                "text": $("> a span", this).text(),
                                "url": $("> a", this).attr("href"),
                                "target": ($("> a", this).attr("target") == undefined) ? "_self" : $("> a", this).attr("target")
                            });
                        });

                        loopIndex++;
                    }

                    // BEYOND 10 CHANNELS
                    if(loopIndex >= 10) {
                        // CREATE OBJECT REFERENCE FOR THE ADDITIONAL CHANNEL
                        channelBarData[loopIndex] = { "channel": { "id": "", "text": "", "url": "", "target": "" }, "sections": [] };

                        // NO MEGA MENU DATA FOR CHANNELS OVER 10 SO MARK IT FALSE - IT'LL BE A NORMAL SECTION DROPDOWN
                        channelBarData[loopIndex].useMenu = false;
                    }
                });

                // BUILD THE CUSTOM CHANNEL BAR
                var customChannelBar = '';

                // ADD HOME CHANNEL
                if($("#navc-HP").length) {
                    customChannelBar +=	'
' +
                                            ' a").attr("href") + '" role="menuitem" data-id="navc-HP">' +
                                                'Home' +
                                            '' +
                                        '';
                }

                // BUILD CHANNELS
                $.each(channelBarData, function(index, menuItem) {
                    if(menuItem.channelExists) {
                        // BEGIN CHANNEL ITEM
                        customChannelBar +=	'
' +
                                                '' +
                                                    '' + menuItem.channel.text + '' +
                                                '';

                        // IF THERE ARE SECTIONS FOR THIS CHANNEL
                        if(menuItem.sections.length) {
                            // BEGIN SECTION LIST
                            customChannelBar += '';

                            // MEGA MENU STRUCTURE
                            if(menuItem.useMenu) {
                                customChannelBar +=	'
' +
                                                        ((menuItem.showImage || $.trim(menuItem.caption) != "" || menuItem.showLink) ? '
' : '') +
                                                            ((menuItem.showImage && menuItem.imageSrc.indexOf("default-man.jpg") === -1) ? '
' : '') +
                                                            (($.trim(menuItem.caption) != "") ? '
' + $.trim(menuItem.caption) + '' : '') +
                                                            ((menuItem.showLink) ? '
' + menuItem.linkText + '' : '') +
                                                        ((menuItem.showImage || $.trim(menuItem.caption) != "" || menuItem.showLink) ? '' : '');

                                // CHECK TO MAKE SURE THAT WE HAVE VALID VALUES FOR SECTION LIMIT
                                var haveValidSectionLimits = menuItem.sectionsPerColumn.every(function(limit) {
                                    return Number.isInteger(parseInt(limit));
                                });

                                if(haveValidSectionLimits) {
                                    var columnClass = ["two", "three", "four"];
                                    var columnSections = "";

                                    $.each(menuItem.sectionsPerColumn, function(index, limit) {
                                        if(menuItem.sections.length) {
                                            // MAKE SURE THAT NO SECTION GET LOST - DUMP ALL SECTIONS THAT ARE LEFT INTO LAST COLUMN
                                            columnSections = (index == (menuItem.sectionsPerColumn.length - 1)) ? menuItem.sections : menuItem.sections.splice(0, parseInt(limit));

                                            customChannelBar += '
' +
                                                                    ((menuItem.columnHeaders[index] != undefined && $.trim(menuItem.columnHeaders[index]) != "") ? '
' + $.trim(menuItem.columnHeaders[index]) + '' : '') +
                                                                    '' +
                                                                        buildSections(columnSections) +
                                                                    '' +
                                                                '';
                                        }

                                        // KILL THE LOOP AFTER THREE ITERATIONS - WE ONLY HAVE THREE COLUMNS
                                        if(index == 2) return false;
                                    });
                                }

                                // THERE'S A BAD VALUE PROVIDED IN THE SECTION COLUMN LIMITS SO WE'LL JUST PUT ALL SECONDS INTO ONE COLUMN
                                else {
                                    customChannelBar += '
' +
                                                            ((menuItem.columnHeaders[0] != undefined && $.trim(menuItem.columnHeaders[0]) != "") ? '
' + $.trim(menuItem.columnHeaders[0]) + '' : '') +
                                                            '' +
                                                                buildSections(menuItem.sections) +
                                                            '' +
                                                        '';
                                }

                                customChannelBar +=	'';
                            }

                            // REGULAR SECTION LIST
                            else {
                                customChannelBar += buildSections(menuItem.sections);
                            }

                            // CLOSE SECTION LIST
                            customChannelBar += '';
                        }

                        // CLOSE CHANNEL ITEM
                        customChannelBar += '';
                    }
                });

                // ADD CALENDAR CHANNEL
                if($("#navc-CA").length) {
                    customChannelBar += '
 a").attr("href")) > -1) ? ' active' : '') + '">' +
                    						' a").attr("href") + '" role="menuitem" data-id="navc-CA">Calendar' +
                                        '';
                }

                $("#gb-channel-list").html('' + customChannelBar + '');

                // BIND EVENTS
                // CHANNEL HOVER/FOCUS
                $("#gb-channel-list").on("mouseenter focusin", ".gb-channel", function(e) {
                    $(this).addClass("hover");
                    if($("> a", this).attr("aria-haspopup") == "true") $("> a", this).attr("aria-expanded", "true");
                    $(".gb-sections", this).attr("aria-hidden", "false").stop(false, true).slideDown(300);
                });

                // CHANNEL HOVER OUT/BLUR
                $("#gb-channel-list").on("mouseleave focusout", ".gb-channel", function(e) {
                    var channel = this;

                    setTimeout(function() {
                        if(!$(channel).find(":focus").length) {
                            $(channel).removeClass("hover");
                            if($("> a", channel).attr("aria-haspopup") == "true") $("> a", channel).attr("aria-expanded", "false");
                            $(".gb-sections", channel).attr("aria-hidden", "true").stop(false, true).slideUp(300);
                            $(".gb-sections a").attr("tabindex", "-1");
                        }
                    }, 100);
                });

                // CHANNEL KEYS
                $("#gb-channel-list").on("keydown", ".gb-channel > a", function(e) {
                    switch(e.keyCode) {
                        // CONSUME UP ARROW
                        case _this.KeyCodes.up:
                            e.preventDefault();

                            $("+ .gb-sections li:last-child > a", this).attr("tabindex", "0").focus();

                        break;

                        // CONSUME DOWN ARROW
                        case _this.KeyCodes.down:
                            e.preventDefault();

                            $("+ .gb-sections a:first", this).attr("tabindex", "0").focus();

                        break;

                        // CONSUME LEFT ARROW
                        case _this.KeyCodes.left:
                            e.preventDefault();

                            // IS FIRST ITEM
                            if($(this).parent().is(":first-child")) {
                                // FOCUS LAST ITEM
                                $(this).closest("#gb-mega-menu").find(".gb-channel:last-child > a").focus();
                            } else {
                                // FOCUS PREVIOUS ITEM
                                $(this).parent().prev(".gb-channel").find("> a").focus();
                            }
                        break;

                        // CONSUME RIGHT ARROW
                        case _this.KeyCodes.right:
                            e.preventDefault();

                            // IS LAST ITEM
                            if($(this).parent().is(":last-child")) {
                                // FOCUS FIRST ITEM
                                $(this).closest("#gb-mega-menu").find(".gb-channel:first-child > a").focus();
                            } else {
                                // FOCUS NEXT ITEM
                                $(this).parent().next(".gb-channel").find("> a").focus();
                            }
                        break;

                        // CONSUME HOME KEY
                        case _this.KeyCodes.home:
                            e.preventDefault();

                            // FOCUS FIRST ITEM
                            $(this).closest("#gb-mega-menu").find(".gb-channel:first-child > a").focus();
                        break;

                        // CONSUME END KEY
                        case _this.KeyCodes.end:
                            e.preventDefault();

                            // FOCUS LAST ITEM
                            $(this).closest("#gb-mega-menu").find(".gb-channel:last-child > a").focus();
                        break;

                        // CONSUME SHIFT+TAB
                        case _this.KeyCodes.tab:
                            if(e.shiftKey) {
                                if(!$(this).parent().is(":first-child")) {
                                    e.preventDefault();

                                    $(this).parent().prev(".gb-channel").find("> a").focus();
                                }
                            }
                        break;

                        // CONSUME SPACE
                        case _this.KeyCodes.space:
                            e.preventDefault();

                            // TOGGLE DROPDOWN
                            if($(this).attr("aria-expanded") == "false") {
                                $(this).attr("aria-expanded", "true").parent().addClass("hover").find(".gb-sections").attr("aria-hidden", "false").slideDown(300, "swing");
                            } else {
                                $("+ .gb-sections", this).find("a").attr("tabindex", "-1");
                                $(this).attr("aria-expanded", "false").parent().removeClass("hover").find(".gb-sections").attr("aria-hidden", "true").slideUp(300, "swing");
                            }
                        break;
                    }
                });

                // SECTION KEYS
                $(".gb-sections").each(function() {
                    var sectionLink = $("a", this);
                    var firstSectionLinkIndex = $(sectionLink).index($(sectionLink).first());
                    var firstSectionLink = $(sectionLink).eq($(sectionLink).index($(sectionLink).first()));
                    var lastSectionLinkIndex = $(sectionLink).index($(sectionLink).last());
                    var lastSectionLink = $(sectionLink).eq($(sectionLink).index($(sectionLink).last()));

                    $(sectionLink).keydown(function(e) {
                        var thisIndex = sectionLink.index(this);
                        var newIndex = -1;

                        switch(e.keyCode) {
                            // CONSUME UP ARROW
                            case _this.KeyCodes.up:
                                e.preventDefault();

                                // IS FIRST ITEM
                                if($(this).is(firstSectionLink)) {
                                    $(this).closest(".gb-sections").find("a").attr("tabindex", "-1");
                                    $(this).closest(".gb-channel").find("> a").focus();
                                }

                                // FOCUS PREVIOUS ITEM
                                else {
                                    $(this).closest(".gb-sections").find("a").attr("tabindex", "-1");
                                    sectionLink.eq(thisIndex - 1).attr("tabindex", "0").focus();
                                }
                            break;

                            // CONSUME DOWN ARROW
                            case _this.KeyCodes.down:
                                e.preventDefault();

                                // IS LAST ITEM
                                if($(this).is(lastSectionLink)) {
                                    $(this).closest(".gb-sections").find("a").attr("tabindex", "-1");
                                    sectionLink.eq(firstSectionLinkIndex).attr("tabindex", "0").focus();
                                }

                                // FOCUS NEXT ITEM
                                else {
                                    $(this).closest(".gb-sections").find("a").attr("tabindex", "-1");
                                    sectionLink.eq(thisIndex + 1).attr("tabindex", "0").focus();
                                }
                            break;

                            // CONSUME LEFT ARROW
                            case _this.KeyCodes.left:
                                e.preventDefault();

                                if($(this).closest(".gb-channel").is(":first-child")) {
                                    $(this).closest("#gb-mega-menu").find(".gb-channel:first-child > a").focus();
                                } else {
                                    $(this).closest(".gb-channel").prev(".gb-channel").find("> a").focus();
                                }
                            break;

                            // CONSUME RIGHT ARROW
                            case _this.KeyCodes.right:
                                e.preventDefault();

                                if($(this).closest(".gb-channel").is(":last-child")) {
                                    $(this).closest("#gb-mega-menu").find(".gb-channel:last-child > a").focus();
                                } else {
                                    $(this).closest(".gb-channel").next(".gb-channel").find("> a").focus();
                                }
                            break;

                            // CONSUME HOME KEY
                            case _this.KeyCodes.home:
                                e.preventDefault();

                                // FOCUS FIRST ITEM
                                $(this).closest(".gb-sections").find("a").attr("tabindex", "-1");
                                $(this).closest(".gb-sections").find("li:first-child > a").attr("tabindex", "0").focus();
                            break;

                            // CONSUME END KEY
                            case _this.KeyCodes.end:
                                e.preventDefault();

                                // FOCUS LAST ITEM
                                $(this).closest(".gb-sections").find("a").attr("tabindex", "-1");
                                $(this).closest(".gb-sections").find("li:last-child > a").attr("tabindex", "0").focus();
                            break;

                            // CONSUME ESC KEY
                            case _this.KeyCodes.esc:
                                e.preventDefault();

                                // FOCUS DROPDOWN BUTTON AND CLOSE DROPDOWN
                                $(this).closest(".gb-channel").find("> a").focus();
                                $(this).closest(".gb-sections").find("a").attr("tabindex", "-1");
                                $(this).closest(".gb-channel").find("> a").attr("aria-expanded", "false").parent().removeClass("hover").find(".gb-sections").attr("aria-hidden", "true").slideUp(300, "swing");
                            break;

                            // CONSUME SPACE KEY
                            case _this.KeyCodes.space:
                                e.preventDefault();

                                $(this).click();
                            break;

                            // CONSUME SHIFT+TAB
                            case _this.KeyCodes.tab:
                                if(e.shiftKey) {
                                    e.preventDefault();

                                    if($(this).closest(".gb-channel").is(":first-child")) {
                                        $(this).closest("#gb-mega-menu").find(".gb-channel:first-child > a").focus();
                                    } else {
                                        $(this).closest(".gb-channel").prev(".gb-channel").find("> a").focus();
                                    }
                                }
                            break;
                        }
                    });
                });

                function buildSections(sectionList) {
                    var sections = "";
                    $.each(sectionList, function(index, section) {
                        sections +=	'
' +
                                        '' +
                                            '' + section.text + '' +
                                        '' +
                                    '';
                    });

                    return sections;
                }
            }
        },

        "SetChannelDropdownPositions": function() {
        	$(".gb-channel").each(function() {
            	var bottomOffset = $(this).position().top + $(this).height();
                $(".gb-sections", this).css("top", bottomOffset);
            });
        },

        "Body": function() {
            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

