							'}' +
                            '#gb-header-outer {' +
                                'background: -webkit-linear-gradient(top, rgba(' + this.HexToRgb("#0E3C77") + ', ' + headerGradientOpacity1 + ') 0%, rgba(' + this.HexToRgb("#0E3C77") + ', ' + headerGradientOpacity2 + ') 100%);' +
                                'background: linear-gradient(to bottom, rgba(' + this.HexToRgb("#0E3C77") + ', ' + headerGradientOpacity1 + ') 0%, rgba(' + this.HexToRgb("#0E3C77") + ', ' + headerGradientOpacity2 + ') 100%);' +
                            '}' +
                            '#gb-footer-outer {' +
                                'background: -webkit-linear-gradient(top, rgba(' + this.HexToRgb(" #0A2A53") + ', ' + footerGradientOpacity1 + ') 0%, rgba(' + this.HexToRgb(" #0A2A53") + ', ' + footerGradientOpacity2 + ') 100%);' +
                                'background: linear-gradient(to bottom, rgba(' + this.HexToRgb(" #0A2A53") + ', ' + footerGradientOpacity1 + ') 0%, rgba(' + this.HexToRgb(" #0A2A53") + ', ' + footerGradientOpacity2 + ') 100%);' +
