                                	'background: linear-gradient(to bottom, rgba(' + this.HexToRgb("#0E3C77") + ', ' + regionCDGradientOpacity1 + ') 0%, rgba(' + this.HexToRgb(" #0A2A53") + ', ' + regionCDGradientOpacity2 + ') 100%);' +
                                '}' +
                            '}'
                })
            );
        },

        "HexToRgb": function(h) {
            var r = parseInt((this.CutHex(h)).substring(0,2),16);
            var g = parseInt((this.CutHex(h)).substring(2,4),16);
            var b = parseInt((this.CutHex(h)).substring(4,6),16);
            return r + ', ' + g + ', ' + b;
        },

        "CutHex": function(h) {
            return (h.charAt(0)=="#") ? h.substring(1,7):h;
        },

        "HeadlinesSlider": function() {
        	$(".hp-content.one .ui-widget.app.headlines").csArticleSlider({
            	"viewerNum": [3, 2, 2, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "transitionSpeed": 1, // SECONDS
                "slideFullView": true
            });

            $(".hp-content.one .ui-widget.app.headlines").each(function() {
            	$(".ui-articles li", this).each(function() {
                	if($("img[src]", this).length) {
                    	var imgSrc = $("img", this).attr("src");
                    	$(this).addClass("has-thumb");
                        $(".ui-article", this).children().not($(".ui-article-thumb", this)).wrapAll('

');
                        $(".cs-headline-detail", this).children().not($(".ui-article-title", this)).wrapAll('

');
                    } else {
                    	$(".ui-article-thumb", this).remove();
                        $(this).addClass("no-thumb");
                        $(".ui-article", this).children().wrapAll('

');
                        $(".cs-headline-detail", this).children().not($(".ui-article-title", this)).wrapAll('

');
                    }
                });

                $(".ui-widget-header", this).append($(".more-link", this).clone().removeAttr("id"));
            });

            // ADD/REMOVE HOVER CLASS FOR HOVER/FOCUS/TOUCH
			$(document).on("mouseenter focusin touchstart", ".hp-content.one .ui-widget.app.headlines .ui-articles > li .ui-article", function(e) {
            	$(this).addClass("hover");
            }).on("mouseleave focusout touchend", ".hp-content.one .ui-widget.app.headlines .ui-articles > li .ui-article", function(e) {
            	$(this).removeClass("hover");
            });
        },

