        "Search": function() {
            var _this = this;

            $(document).on("click keydown", "#gb-search-toggle", function(e){
                if(_this.AllyClick(e)) {
                	e.preventDefault();

                    if($(this).attr("aria-expanded") == "false") {
                    	$(this).attr("aria-expanded", "true");
                        $("#gb-search-outer").attr("aria-hidden", "false");

                        setTimeout(function() {
                        	$("#gb-search-input").focus();
                        }, 300);
                    } else {
                    	$(this).attr("aria-expanded", "false");
                        $("#gb-search-outer").attr("aria-hidden", "true");
                    }
                }
            });

            $(document).on("click keydown", "#gb-search-button", function(e){
                if(_this.AllyClick(e)) {
                	e.preventDefault();

                    $("#gb-search-form").submit();
                } else if(e.type == "keydown" && e.keyCode == _this.KeyCodes.tab && !e.shiftKey) {
                	e.preventDefault();

                    $("#gb-search-toggle").focus();
                }
            });

            $(document).on("keydown", "#gb-search-input", function(e){
                if(e.keyCode == _this.KeyCodes.tab && e.shiftKey) {
                	e.preventDefault();

                    $(".cs-mystart-dropdown.translate .cs-selector").focus();
                }
            });

            $("#gb-search-form").submit(function(e){
                e.preventDefault();

                if($.trim($("#gb-search-input").val()) != "" && $.trim($("#gb-search-input").val()) != "Search...") {
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=2689&SearchString=" + $("#gb-search-input").val();
                }
            });

            $("#gb-search-outer").focusout(function() {
				setTimeout(function() {
                	if(!$("#gb-search-outer").find(":focus").length) {
                    	$("#gb-search-toggle").attr("aria-expanded", "false");
                    	$("#gb-search-outer").attr("aria-hidden", "true");
                    }
                }, 100);
			});

            $("#gb-search-input").focus(function() {
                if($(this).val() == "Search...") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search...");
                }
            });
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

    // IE11 POLYFILL FOR .isInteger
    Number.isInteger = Number.isInteger || function(value) {
        return typeof value === "number" && 
               isFinite(value) && 
               Math.floor(value) === value;
    };

