div.script:
var script = document.createElement('script');
script.src = "https://cdnjs.cloudflare.com/ajax/libs/tota11y/0.1.6/tota11y.min.js";

if(getQueryString('tota11y') === 'true') {
	document.head.append(script);
}	

function getQueryString (field, url) {
	var href = url ? url : window.location.href;
	var reg = new RegExp( '[?&]' + field + '=([^&#]*)', 'i' );
	var string = reg.exec(href);
	return string ? string[1] : null;
}

if (!('placeholder' in document.createElement('input'))) {
	var el = document.getElementById('q');
	var orig = 'Search site';
	el.value = orig;

	el.onclick = function() {
		if (this.value==orig) this.value='';
	}

	el.onblur = function() {
		if (this.value=='') this.value=orig;
	}
}

body.script:
	var jq17 = jQuery.noConflict(true);
	jq17(function() {
		var pull 		= jq17('#topnav_mobile');
			menu 		= jq17('#topnav ul');
			menuHeight	= menu.height();

		jq17(pull).on('click', function(e) {
			e.preventDefault();
			menu.slideToggle();
		});

		jq17(window).resize(function(){
    		var w = jq17(window).width();
    		if(w > 550 && menu.is(':hidden')) {
    			menu.removeAttr('style');
    		}
		});
	});

  if (window.location.href.indexOf('/apps/contact/') != -1){
