$(document).ready(function() {
    $('#jobtable').DataTable( {
        initComplete: function () {
        var column_num = 1;
		var mymenutitle = '';
            this.api().columns().every( function () {

                var column = this;


//var check = column().title();
column_num++;

if((column_num == '3') || (column_num == '4')){

if(column_num == '3'){
mymenutitle = 'District';
}
if(column_num == '4'){
mymenutitle = 'Type';
}



                var select = $(''+mymenutitle+'')
                    .appendTo( $(column.header()).empty() )
                    .on( 'change', function () {
                        var val = $.fn.dataTable.util.escapeRegex(
                            $(this).val()
                        );

                        column
                            .search( val ? '^'+val+'$' : '', true, false )
                            .draw();
                    } );

}

                column.data().unique().sort().each( function ( d, j ) {

if(((column_num == '3') || (column_num == '4')) && (d != '')){

                    select.append( ''+d+'' )


}




                } );
            } );
        }
    } );
} );

 $('#jobtable').css("font-size", "15px");

});

