    },

    update: function() {
      var obj = this;
      obj.$outerWrap = $(obj.outerWrapSelector);
      obj.$userLinks = $(obj.userLinksSelector);
      obj.$loginToAdd = $(obj.loginToAddSelector);
      obj.$addComment = $(obj.addCommentSelector);
