    },

    updateUserLinks: function(loggedIn, username) {
      var obj = this;
      if (loggedIn) {
        obj.$userLinks.html('Hello, ' + username + '! | Sign Out');
      } else {
        obj.$userLinks.html("Sign In or Register");
      }
    },

