$(document).ready(function(){	

	//Sticky Header
	if($(window).width() > 950 ){
		
	var $win = $(window),
	    $main = $('#page'),
	    $nav = $('#menubar'),
	    navHeight = $nav.outerHeight(),
	    navPos = $nav.offset().top,
	    fixedClass = 'is-fixed',
		fixedClassNew = 'fixed-new',
		fixedClassOld = 'fixed-old';

	$win.on('load scroll', function() {
		var value = $(this).scrollTop();
		if ( value > navPos ) {
			
			//New Toolbar
			if($(".CMS_adminBar").length){
				$nav.addClass(fixedClassNew);
				$main.css('margin-top', navHeight);
				$("#sticky-header").css("top", "88px");
				$("#sticky-header").css("display","block")
			}
			//Old Toolbar
			else if($("#ctl00_ContentPlaceHolder1_ctl00_ctl00_menu").length){
				$nav.addClass(fixedClassOld);
				$main.css('margin-top', navHeight);
				$("#sticky-header").css("top", "73px");
				$("#sticky-header").css("display","block")
			}
			//No Toolbar
			else{
				$nav.addClass(fixedClass);
				$main.css('margin-top', navHeight);
				$("#sticky-header").css("top", "38px");
				$("#sticky-header").css("display","block")
			}
		} else {
			
			//New Toolbar
			if($(".CMS_adminBar").length){
				$nav.removeClass(fixedClassNew);
				$main.css('margin-top', '0');
				$("#sticky-header").css("display","none")
			}
			//Old Toolbar
			else if($("#ctl00_ContentPlaceHolder1_ctl00_ctl00_menu").length){
				$nav.removeClass(fixedClassOld);
				$main.css('margin-top', '0');
				$("#sticky-header").css("display","none")
			}
			//No Toolbar
			else{
				$nav.removeClass(fixedClass);
				$main.css('margin-top', '0');
				$("#sticky-header").css("display","none")
			}
		}
	});
		
