jQuery(document).ready(function($) {
    var mpAccordion = $(".motopress-accordion-obj#5ea027f9b1af0");
    if (mpAccordion.data("uiAccordion")) {
        mpAccordion.accordion("destroy");
    }
    mpAccordion.accordion({
        active: false,
        collapsible: true,
        header: "> div > h3",
        heightStyle: "content"
    });
});

