ul.script:
function googleTranslateElementInit() {
new google.translate.TranslateElement({pageLanguage: 'en', layout: google.translate.TranslateElement.InlineLayout.SIMPLE, multilanguagePage: true}, 'google_translate_element');
}

header.script:
$(document).ready(function() {
    // Only if announcements exist
    if ( $( "#announcements" ).length ) {
        // Iterate over announcements and show them if the dismissal cookie hasn't been set
        $("#announcements").children("div.alert").each(function(e) {
            var announcementCookie = "dismiss-announcement-" + $(this).data("slug")
            if (document.cookie.indexOf(announcementCookie) == -1) {
                $(this).removeClass("hide");
            }
        });

        // On dismissal of an alert, persist the dismissal for that one alert, for the duration of the session
        $(".alert .close").on("click", function(e) {
           document.cookie = "dismiss-announcement-" + $(this).parent().data("slug") + "=" + Math.round(+new Date()/1000) + ";path=/";
        });
    }
});

footer.script:
var isVisible = false;
    $(window).scroll(function(){
     var shouldBeVisible = $(window).scrollTop()>200;
     if (shouldBeVisible && !isVisible) {
          isVisible = true;
          $('#bottom-navbar').show();
     } else if (isVisible && !shouldBeVisible) {
          isVisible = false;
          $('#bottom-navbar').hide();
    }
});

