        "Search": function() {
        	var defSearchText = "SEARCH...";

            var searchBox = $('#swsitesearch-txt-searchstring');

            searchBox
            	.unbind()
                .attr("aria-label", "Search input")
                .attr("placeholder", '')
                .attr('aria-hidden', 'true')
                .attr('tabindex', -1)
                .val(defSearchText)
                .focus(function() {
                    if($(this).val() == defSearchText) {
                        $(this).val("");
                    }
                })
                .blur(function() {
                    if($(this).val() == "") {
                        $(this).val(defSearchText);
                    }
                });

            $('#gb-search-trigger').off('click').click(function(e){
				if( $('#gb-search').hasClass('open') ){
                	$('#gb-search').removeClass('open');
                    $('#gb-search #swsitesearch-txt-searchstring')
                    	.attr('aria-hidden', 'true')
                        .attr('tabindex', -1)
                        .animate({'width': 0}, 300);

                    $(this)
                    	.attr('aria-expanded', 'false')
                        .attr('aria-label', 'Open Search Input');

                    $('#gb-custom-mystart .mystart-label').attr('tabindex', 0);
                }else{
                	var searchWidth = ($(window).width() >= 1280) ? 463 : 250;

                	$('#gb-search').addClass('open');
            		$('#gb-search #swsitesearch-txt-searchstring')
                    	.attr('tabindex', 0)
                    	.attr('aria-hidden', 'false')
                        .animate({'width': searchWidth}, 300, function(e){
                        	$(this).focus();
                        });

                    $(this)
                    	.attr('aria-expanded', 'true')
                        .attr('aria-label', 'Close Search Input');

                    $('#gb-custom-mystart .mystart-label').attr('tabindex', -1);
                }
            });

            (function() {
                /* Custom Hourly - Specific site search results - Brenton Kelly - 12.21.17 */
                var searchID = '014714281079215143459:f-bdtez5i9y';
                var keyCodes = { 'tab': 9, 'enter': 13, 'esc': 27, 'space': 32, 'end': 35, 'home': 36, 'left': 37, 'up': 38, 'right': 39, 'down': 40 };
