                });

                function allyClick(event) {
                    if(event.type == 'click') {
                        return true;
                    } else if(event.type == 'keydown' && (event.keyCode == keyCodes.space || event.keyCode == keyCodes.enter)) {
                        return true;
                    } else {
                        return false;
                    }
                }
            })();
