        "GlobalIconText": function(action, scope){
        	switch(action){
            	case 'show':
                	var textWidth = $('.text', scope).css('width', 'auto').width();
                    $('.text', scope)
                        .css('width', 0)
                        //.stop(true)
                        .animate({
                            'width': textWidth,
                            'padding-left': 15,
                            'padding-right': ($(window).width() >= 1024) ? 50 : 0
                        }, 400);
                	break;
                case 'hide':
                	$('.text', scope)
                        .stop(true)
                        .animate({
                            'width': 0,
                            'padding-left': 0,
                            'padding-right': 0
                        }, 400);
                	break;
                default:
                	break;
            }
        },
