        "GoogleMap": function(){
            var winWidth = $(window).width();
            var newWidthNoOffset = ($(window).width() - 1280) / 2;
            var maxWidth = 0;

            if( newWidthNoOffset < 0 ) newWidthNoOffset = 0;

            if( winWidth >= 1024 ){
                maxWidth = newWidthNoOffset + 397;
            }else{
                if( winWidth > 640 ){
                    maxWidth = newWidthNoOffset + 147;
                }else{
                	maxWidth = newWidthNoOffset + 137;
                }
            }

            $('#map-canvas')
            	.attr('tabindex', -1)
                .attr('aria-hidden', 'true')
				.css('width', maxWidth);
        },

