                extra += '&caching=' + wixBiSession.caching + '&is_cached=' + wixBiSession.isCached;
            }
            wixBiSession.sendBeacon(beatUrl
                + '&et=' + et
                + (name ? '&event_name=' + encodeURIComponent(name) : '')
                + '&ts=' + ts
                + '&tts=' + tts
                + '&vsi=' + wixBiSession.viewerSessionId
                + '&rid=' + wixBiSession.requestId
