div.script:
var rules;
for (i = 0; i < document.styleSheets.length; i++) {
	if (document.all && document.styleSheets[i].href == "/shared/main.css")
		rules = document.styleSheets[i].rules;
	else if (document.styleSheets[i].href && document.styleSheets[i].href.indexOf(document.location.host + "/shared/main.css") >= 0)
		rules = document.styleSheets[i].cssRules;
}

if (rules != null) {
	var hasPageNav = false;
	for (i = 0; i < rules.length; i++) {
		if (rules[i].selectorText && rules[i].selectorText.toLowerCase().indexOf("#pagenav") >= 0) {
			hasPageNav = true;
			break;
		}
	}

	if (!hasPageNav) {
		var color;
		for (i = 0; i < rules.length; i++) {
			if (rules[i].selectorText && rules[i].selectorText.toLowerCase().indexOf(".dark") >= 0 && rules[i].selectorText.toLowerCase().indexOf(".dark a") < 0)
				color = rules[i].style.background;
			if (color == "")
				color = rules[i].style.backgroundColor;
		}

		if (color) {
			if (color.indexOf("rgb(") >= 0) {
				color = color.substring(color.indexOf("rgb("), color.length);
				color = color.substring(0, color.indexOf(")") + 1);
			} else {
				color = color.substring(color.indexOf("#"), color.length);
				if (color.indexOf(" ") > 0)
					color = color.substring(0, color.indexOf(" "));
			}

			var sn = document.getElementById("pageNav");
			sn.style.border = "1px solid " + color;
		}
	}
}

link.script:
function ENCrossURL() {
	var crossURL = "http://www.magnoliapublicschools.org";
	var d = document.location.host;
	var a = document.getElementsByTagName("a");
	for (i = 0; i < a.length; i++) {
		var h = a[i].href;
		if (h.indexOf(crossURL) == 0 && (h.indexOf(".jsp?") >= 0 || h.lastIndexOf(".jsp") == h.length - 4 || h.lastIndexOf("/") == h.length - 1 || h.substring(crossURL.length).indexOf(".") <= 0) && h.indexOf("_rss.jsp") < 0 && h.indexOf("/apps/events/ical.jsp") < 0 && h.indexOf("_pff.jsp") < 0 && h.indexOf("&pff=1") < 0 && h.indexOf("?pff=1") < 0) {
			var inner = a[i].innerHTML;

			var qm = h.indexOf("?");
			if (qm > 0) {
				var q = h.substring(qm);
				h = h.substring(0, qm) + escape(q);
			}

			a[i].href = "/apps/cross.jsp?wREC_ID=3674&crossPath=" + h.substring(crossURL.length);

			if (a[i].innerHTML != inner)
				a[i].innerHTML = inner;
		}
	}
}

ENCrossURL();

