arch').before($('.sw-mystart-dropdown.translate')); $('.sw-mystart-dropdown.translate .sw-dropdown-arrow').text("▼"); } } } // CALLBACK TO RUN YOUR CUSTOM CODE AFTER EVERYTHING IS READY }); } });

    $(function() {
        CreativeTemplate.Init(); //tip of the iceberg
    });

    var csTemplate = {};

    var CreativeTemplate = {

        /*****
            PROPERTIES
        *****/

        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "DefaultLogoSrc": "",
        "DynamicStyles": "", //This can be added to and if it's not empty it'll be added to the page in a stylesheet
        "MobileMenuBreakpoint": 768,



        /*****
            METHODS
        *****/

// BIG BANG METHOD
        "Init": function() {
            // FOR SCOPE
            window.csTemplate = this;

            this.SetTemplateProps();
            this.SLM(); //will daisy chain the RsMenu function
            this.GlobalIcons();
            this.JsMediaQueries();
            this.MyStart();
            this.Search();
            this.Header();
            this.ChannelBar();
            this.StickyChannelBar();
            this.Body();
            this.Slideshow(); //move this into JsMediaQueries if it isn't present across all of the Breakpoints
            this.AppAccordion();
            this.AppScroller();
            this.Footer();
            this.Toolbar();

            if( this.DynamicStyles != "" ){ //Build out our stylesheet if any of the above items have added styles to it.
                this.BuildStyleSheet();
            }

            //REMOVE DUPLICATE IDs
            $("#sw-mystart-left #ui-btn-myview").remove();
            $("#sw-mystart-right #ui-btn-sitemanager").remove();

            $(window).load(function(){ window.csTemplate.WindowLoad(); });
            $(window).resize(function(){ 
                 window.csTemplate.WindowResize(); 
                 window.csTemplate.CalendarListView();
                 window.csTemplate.StickyChannelBar();
            });

            $(window).scroll(function() {
            	if($(".hp").length) {
                   var hT = $('#hp-content-row-three-outer').offset().top,
                       hH = $('#hp-content-row-three-outer').outerHeight(),
                       wH = $(window).height(),
                       wS = $(this).scrollTop();
                   if (wS > (hT+hH-wH)){
                    $('#hp-content-row-three-outer').addClass("zoom");
                   } else {
                    $('#hp-content-row-three-outer').removeClass("zoom");
                   }
                }   

              if($(window).scrollTop() > 300){
                  $("#gb-scroll-up").fadeIn();
              } else {
                   $("#gb-scroll-up").fadeOut();
              }

              window.csTemplate.StickyChannelBar();

            });



        },


// TOP LEVEL (Window/Document) EVENT HANDLERS
        "WindowLoad": function() {
            if($(window).width() >= 320 && $(window).width() <= 639){
                this.CalendarListView();
            }    
            this.Search();

            $("div.ui-widget.app.navigation li div.bullet").click(function(){
                return true;
            });

            //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
            $(".sw-mystart-dropdown.translate").unbind('hover');
            $(".sw-mystart-dropdown.translate").hover(function(e){

            }, function(e) {
                //e.stopPropagation();
                $(".sw-dropdown-arrow").removeClass("rotate-arrow");
                $(this).removeClass("open");
                $(".sw-dropdown").slideUp("slow", "swing").attr("aria-hidden", "true");
            });

            //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
            $("#sw-mystart-account").unbind('hover');
            $("#sw-mystart-account").hover(function(){

            }, function() {
            	$(this).removeClass("clicked-state");
                $(this).removeClass("open");
                $("#cs-sw-myaccount-list").slideUp("slow", "swing").attr("aria-hidden", "true");
            });

            //UNBIND SYSTEM HOVER EVENT AND ADD CUSTOM HOVER EVENT
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-item ul").stop(true, true);
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

              // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();
        },
        "WindowResize": function() {
            this.JsMediaQueries();
            this.CalendarListView();
        },

//TEMPLATE EVENT HANDLERS
        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
                	$(".hp .global-icon").each(function(){
                       var textHeight = $(".text",this).height();
                       $("span",this).css("top",textHeight);
                       $(this).hover(function(){
                            $("span",this).css("top","0px");
                       }, function(){
                            $("span",this).css("top",textHeight);
                       });
                       $(this).focusin(function(){
                            $("span",this).css("top","0px");
                       });
                       $(this).focusout(function(){
                            $("span",this).css("top",textHeight);
                       });

                    });
                break;
                case "768":

                break;
                case "640":

                break;
                case "480":

                break;
                case "320":

                break;
            }
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

// INFORMATION SETTERS
        "SetTemplateProps": function() {
            // MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },
        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },


//TEMPLATE METHODS
        "MyStart": function() {

        	var template = this;

            var channelHeight = $("#gb-channel-list-outer").height();
            var mystartOffset = 22 + channelHeight + 20;
            $(".gb-mystart.top").css("top",mystartOffset);

            $(window).resize(function(){
            	var channelHeight = $("#gb-channel-list-outer").height();
            	var mystartOffset = 22 + channelHeight + 20;
            	$(".gb-mystart.top").css("top",mystartOffset);
            });

            //TO FIX THE ISSUE OF SEARCH NOT ALLOWING SPACES
            $("#sw-mystart-search").unbind();

            //APPEND SEARCH TO GB-SEARCH-BOTTOM
            $("#sw-mystart-search").appendTo("#gb-search-container");

            //ADD ROLE AND ARIA LABEL TO SEARCH BUTTON
            $("#sw-search-button").attr("role","button").attr("aria-label","Submit Search"); 

             $(".gb-mystart-top-inner #sw-search-input").attr("tabindex","-1");

            $(".gb-mystart-top-inner #gb-search-btn").on("click keydown", function(e) {
            	searchHoverTimer = "";
                if(template.AllyClick(e)) {
                    e.preventDefault();
                    if($("#gb-search-container").hasClass("open")){
                    clearTimeout(searchHoverTimer);
                        $("#gb-search-container").removeClass("open").attr("aria-hidden","true");
                        	$(this).attr("aria-expanded","false").focus();
                    } else {
                        $("#gb-search-container").addClass("open").attr("aria-hidden","false");
                        searchHoverTimer = setTimeout(function(){
                        	$(".gb-mystart-top-inner #sw-search-input").focus().attr("tabindex","0");
                        	$(this).attr("aria-expanded","true");
                        }, 1000);
                    }
                }
            });  

            this.Translator();

            $(".sw-mystart-dropdown.translate").off();
            $(".sw-mystart-dropdown.translate").click(function() {
                  $(this).toggleClass("open").attr("aria-expanded", ($(this).attr("aria-expanded") == "true") ? "false" : "true");
                  $(".sw-dropdown-arrow",this).toggleClass("open");
                  $(".sw-dropdown", this).slideToggle("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");  
                  $(".sw-dropdown-list", this).attr("aria-hidden", ($(".sw-dropdown-list", this).attr("aria-hidden") == "true") ? "false" : "true"); 
            });

            $(".sw-mystart-dropdown").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME DOWN ARROW
                    case template.KeyCodes.down:
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();
                        $(".sw-dropdown", this).slideToggle("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");
                        $(".sw-dropdown-list", this).attr("aria-hidden", ($(".sw-dropdown-list", this).attr("aria-hidden") == "true") ? "false" : "true"); 

                        // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                          if(!$(this).hasClass("open")) {
                              // TOGGLE THE DROPDOWN
                              $(this).addClass("open").attr("aria-expanded","true");
                              $(".sw-dropdown-list li:first-child a", this).focus();

                          } else {
                                $(this).removeClass("open").attr("aria-expanded","false");
                          }
                          $(".sw-dropdown-list li a", this).attr("tabindex","0");
                        // FOCUS FIRST ITEM

                    break;
                }
            });

            $(".sw-mystart-dropdown .sw-dropdown-list li a").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS LAST ITEM
                            $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                    break;
                }
            });



        },
        "Header": function() {
            //ADD LOGO
            var logoCheck = "true";
            var logoSrc = jQuery.trim("/cms/lib/CA01000875/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
            if(logoCheck == "true") {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").append('').removeClass("hidden");
                } else {
                    $("#gb-logo").append('').removeClass("hidden");
                }
            }

            //ADD SITENAME
            var siteNameOne = jQuery.trim('');
            var siteNameTwo = jQuery.trim('');
            var siteNameThree = jQuery.trim('');
            if((siteNameOne == "") && (siteNameTwo == "") && (siteNameThree == "")) {
                var splitLen = 2;
                var siteName = "Tamalpais Union High School District";
                siteName = siteName.split(" ");
                var siteNameLength = siteName.length;
                if(siteNameLength > 2){
                    siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                } else {
                    siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                }
                siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
                $("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
            } else if((siteNameOne != "") && (siteNameTwo == "")) {
                $("#gb-sitename").prepend('

' + siteNameOne + '');
            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend('

' + siteNameTwo + '');
            } else if((siteNameOne != "") && (siteNameTwo != "") && (siteNameThree == "")) {
                $("#gb-sitename").prepend('

' + siteNameOne + '

' + siteNameTwo + '');
            } else if((siteNameOne != "") && (siteNameTwo != "") && (siteNameThree != "")) {
                $("#gb-sitename").prepend('

' + siteNameOne + '

' + siteNameTwo + '
' + siteNameThree + '');
            }
        },
