            $("#sw-content-container1.region.ui-hp div.ui-widget.app.headlines li").each(function() {
                var imgWidth = $(".img img", this).attr("width");
                $(".ui-article-info",this).css("width",imgWidth + "px");
                if($(".more-link",this).length){
                	$(".more-link",this).prependTo("#sw-content-container1.region.ui-hp div.ui-widget.app.headlines .ui-widget-footer");
                    $(this).remove();
                }
            });

            $("#sw-content-container1.region.ui-hp div.ui-widget.app.headlines li").each(function() {
                var pHeight = $(".ui-article-info .ui-article-description", this).height();
                if($(".ui-article-thumb",this).length){
                	$(".ui-article-info",this).css("margin-bottom",-pHeight - 13);
                }    
            });

            $("#sw-content-container1.region.ui-hp div.ui-widget.app.headlines li").hover(function() {

                	$(".ui-article-info",this).css("margin-bottom","0px");
                },function(){
                	var pHeight = $(".ui-article-info .ui-article-description", this).height();
                	$(".ui-article-info",this).css("margin-bottom",-pHeight - 13);
            });

            $("#sw-content-container1.region.ui-hp div.ui-widget.app.headlines li .ui-article-title a").focus(function() {

                	$(this).parent().parent().css("margin-bottom","0px");
            });

            $("#sw-content-container1.region.ui-hp div.ui-widget.app.headlines li .ui-article-title a").focusout(function() {

                	var pHeight = $(this).parent().parent().find(".ui-article-description").height();
                    $(this).parent().parent().css("margin-bottom",-pHeight - 13);
            });

            //CHANGE HEADLINES RSS TEXT
            $("#sw-content-container1.region.ui-hp div.ui-widget.app.headlines .app-level-social-rss a span").text("Subscribe");


