        "RsMenu": function(schoolList) {
            var mobileSchools = '', additionalHeaders = "", additionalLinks = "";

            for(var i in schoolList) {
                if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                    mobileSchools += '1,' + schoolList[i].schoolName + ',' + schoolList[i].schoolUrl + ', _blank;';
                }
            }

            // ADD GLOBAL ICONS TO MENU
            var menuGlobalIcons = [
                ["Calendar", "[SITECALENDARLINK]"],
                ["Budget", "https://www.tamdistrict.org/page/88"],
                ["Contact Us", "https://www.tamdistrict.org/domain/17"],
                ["Current Job Postings", "https://www.tamdistrict.org/Page/6511"],
                ["Performance", "https://www.tamdistrict.org/Page/7"]
            ];
            var menuIconNum = ("5" == "5;4;3;2;1;0") ? 0 : "5";

            for(var i = 0; i < menuIconNum; i++) {
                var linkCheck = (menuGlobalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : menuGlobalIcons[i][1];
                additionalLinks += "4, " + menuGlobalIcons[i][0] + ", " + linkCheck + ", _self;";
            }

            if(menuIconNum > 0) {
                additionalHeaders = "Popular Links";
            }

            $("body").csRsMenu({
                "breakPoint" : window.csTemplate.MobileMenuBreakpoint,
                "slideDirection" : "right-to-left",
                "primaryColor" : "#1A1A1A",
                "secondaryColor" : "#0C0C0C",
                "accentColor" : "#BABABA",
                "menuButtonParent" : "#gb-channel-list",
                "showMenuBtnText" : "yes",
                "menuBtnText" : "Menu",
                "showSchools" : "no",
                "showTranslate" : "no",
                "translateId" : "none",
                "showAccount" : "yes",
                "usePageListNavigation" : "yes",
                "extraMenuOptions"      : "Schools;" + additionalHeaders, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
                "extraMenuItems"        : mobileSchools + additionalLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
                "siteID" : "1",
                "allLoaded" : function(){ 
                	$("#rs-menu-btn").insertAfter("#gb-header");
                }
            });
        },
