    $(function() {
    	CreativeTemplate.Init();
    });

	var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ShowDistrictHome": true,
        "ShowSchoolList": false, // UPDATES IN SetTemplateProps METHOD
        "ShowTranslate": true,
        "ShowDistrictHome": true,
		"ShowFullSiteButton": false,
        "DynamicStyles": "", //This can be added to and if it's not empty it'll be added to the page in a stylesheet
        "ChannelBarIsSticky": false,
        "LogoOnly": false,

		// METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            if( $('.sp').length > 0 ){
            	$('html').addClass('sp'); //having to do this for passkeys overlay
            }

            this.SetTemplateProps();
            this.MyStart();
            this.JsMediaQueries();
			this.SchoolList();
			this.Translate();
            this.GlobalIcons();
            this.Header();
            this.Search();
            this.ChannelBar();
            this.StickyChannelBar();
            this.Slideshow();
            this.Body();
            this.Shortcuts();
            this.ModEvents();
            this.ArticleSliders();
            this.RsMenu();
            this.AppAccordion();
            this.Footer();
            this.SocialIcons();
            this.ViewFullSite();

            $('[data-remove="remove-"]').remove();

            if( this.DynamicStyles != "" ){ //Build out our stylesheet if any of the above items have added styles to it.
            	this.BuildStylesheet();
            }

            csGlobalJs.OpenInNewWindowWarning();

            $(window).load(function() { _this.WindowLoad(); });
            $(window).resize(function() { _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;

            // SCHOOL LIST CHECK
        	if($(".sw-mystart-dropdown.schoollist").length) this.ShowSchoolList = true;
        },

        "WindowLoad": function() {
			$('.headlines .ui-article').each(function(articleIndex, article){
                if($('.ui-article-thumb img', article).width() >= 280){
                	$(article).addClass('image-text-wrap-640');

                    if($('.ui-article-thumb img', article).width() >= 385){
                        $(article).addClass('image-text-wrap-768');
                    }
                }
            });

            if( $('.sp-column.one').length > 0 && $('.sp-column.one .ui-widget-header').children().length <= 0 ){
            	$('.sp-column.one .ui-widget-header').prepend('
' + $('#gb-channel-list .sw-channel-item.active > a > span').html() + '')
            }

            csGlobalJs.OpenInNewWindowWarning();
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.StickyChannelBar();
        },

        "WindowScroll": function() {
            this.StickyChannelBar();

            if( !$('html').hasClass('cs-scrolled-enough') && $(window).scrollTop() >= 300 ){
            	$('html').addClass('cs-scrolled-enough');

            }else if($('html').hasClass('cs-scrolled-enough') && $(window).scrollTop() < 300){
            	$('html').removeClass('cs-scrolled-enough');
            }
        },

        "JsMediaQueries": function() {
			CreativeTemplate.Shortcuts();

            if(CreativeTemplate.GetBreakPoint() == "desktop"){
            	//desktop
            	if( $('#sw-mystart-mypasskey').hasClass('mobile-move') ){
                    $('#sw-mystart-mypasskey').removeClass('mobile-move');
                    $('#gb-mystart').append($('#sw-mystart-mypasskey'));
                }

                if( $('#gb-logo-outer:not(".logo-only")').hasClass('mobile-move') ){
                    $('#gb-logo-outer:not(".logo-only")').removeClass('mobile-move');
                    $('#gb-main-page-header').prepend($('#gb-logo-outer:not(".logo-only")'));
                }
            }else{
            	//all mobile
            	if( !$('#sw-mystart-mypasskey').hasClass('mobile-move') ){
                    $('#sw-mystart-mypasskey').addClass('mobile-move');
                    $('#gb-channel-list').append($('#sw-mystart-mypasskey'));
                }

                //320 only
                if(CreativeTemplate.GetBreakPoint() == "320"){
                	if( !$('#gb-logo-outer:not(".logo-only")').hasClass('mobile-move') ){
                        $('#gb-logo-outer:not(".logo-only")').addClass('mobile-move');
                        $('#sticky-header-outer').after($('#gb-logo-outer:not(".logo-only")'));
                    }
                }else{
                	//above 320 only
                	if( $('#gb-logo-outer:not(".logo-only")').hasClass('mobile-move') ){
                        $('#gb-logo-outer:not(".logo-only")').removeClass('mobile-move');
                        $('#gb-main-page-header').prepend($('#gb-logo-outer:not(".logo-only")'));
                    }
                }
            }
        },

        "MyStart": function() {
        	// FOR SCOPE
            var _this = this;

			// ADD DISTRICT HOME
            if(this.ShowDistrictHome && $(".sw-mystart-button.home").length) {
            	$(".sw-mystart-button.home #sw-home-icon").remove();
                $(".sw-mystart-button.home > a > span").text("District Home");
                $(".sw-mystart-button.home a").prepend('');
            	$("#gb-mystart").prepend($(".sw-mystart-button.home"));
                $(".sw-mystart-button.home > a").addClass('mystart-label');
            }

            // BUILD USER OPTIONS DROPDOWN
            var userOptionsItems = "";



			// SIGNIN BUTTON
            if($(".sw-mystart-button.signin").length) {
				userOptionsItems += "
" + $(".sw-mystart-button.signin").html() + "";
			}

			// REGISTER BUTTON
            if($(".sw-mystart-button.register").length) {
                userOptionsItems += "
" + $(".sw-mystart-button.register").html() + "";
            }



			// ADD USER OPTIONS DROPDOWN TO THE DOM
            $(".cs-mystart-dropdown.user-options .cs-dropdown-list").html(userOptionsItems);

			// BIND DROPDOWN EVENTS
			this.DropdownActions({
            	"dropdownParent": ".cs-mystart-dropdown.user-options",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });

        },

		"SchoolList": function() {
			// ADD SCHOOL LIST
			if(this.ShowSchoolList) {
				// ADD SCHOOL LIST TO THE DOM	
				$("#gb-mystart .cs-mystart-dropdown.schools .cs-dropdown ul").html($(".sw-mystart-dropdown.schoollist .sw-dropdown-list").html());

				// BIND DROPDOWN EVENTS
				this.DropdownActions({
					"dropdownParent": ".cs-mystart-dropdown.schools",
					"dropdownSelector": ".cs-selector",
					"dropdown": ".cs-dropdown",
					"dropdownList": ".cs-dropdown-list"
				});
			}else{
            	$('#gb-mystart .cs-mystart-dropdown.schools').remove();
            }
		},

		"Translate": function() {
			// ADD TRANSLATE
            if(this.ShowTranslate) {
                $(".cs-mystart-dropdown.translate .cs-dropdown").creativeTranslate({
                    "type": 2, // 1 = FRAMESET, 2 = BRANDED, 3 = API
					"languages": [ // ["ENGLISH LANGUAGE NAME", "TRANSLATED LANGUAGE NAME", "LANGUAGE CODE"]
					],
					"advancedOptions": {
						"addMethod": "append", // PREPEND OR APPEND THE TRANSLATE ELEMENT
						"dropdownHandleText": "Translate this page...", // ONLY FOR FRAMESET AND API VERSIONS AND NOT USING A CUSTOM ELEMENT
						"customElement": { // ONLY FOR FRAMESET AND API VERSIONS
							"useCustomElement": false, 
							"translateItemsList": true, // true = THE TRANSLATE ITEMS WILL BE AN UNORDERED LIST, false = THE TRANSLATE ITEMS WILL JUST BE A COLLECTION OF  TAGS
							"customElementMarkup": "" // CUSTOM HTML MARKUP THAT MAKES THE CUSTOM TRANSLATE ELEMENT/STRUCTURE - USE [$CreativeTranslateListItems$] ACTIVE BLOCK IN THE MARKUP WHERE THE TRANSLATE ITEMS SHOULD BE ADDED
						},
						"apiKey": "", // ONLY FOR API VERSION
						"brandedLayout": 1 // 1 = VERTICAL (TEXT UNDER SELECT), 2 = HORIZONTAL (TEXT BESIDE SELECT), 3 = NON-RESPONSIVE MEGA DROPDOWN
					},
					"translateLoaded": function() {}
                });

                // BIND DROPDOWN EVENTS
                this.DropdownActions({
                    "dropdownParent": ".cs-mystart-dropdown.translate",
                    "dropdownSelector": ".cs-selector",
                    "dropdown": ".cs-dropdown",
                    "dropdownList": ".cs-dropdown-list"
                });
            }else{
            	$('.cs-mystart-dropdown.translate').remove();
            }
		},

		"DropdownActions": function(params) {
        	// FOR SCOPE
            var template = this;

            var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;

            $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(dropdownParent).on("click", dropdownSelector, function(e) {
                e.preventDefault();

                if($(this).parent().hasClass("open")){
                	$("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
                }
        	});

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                        	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if($("+ " + dropdown + " " + dropdownList + " a").length) {
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        	$(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                    	if(e.shiftKey) {
                        	e.preventDefault();

                        	// FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;
                }
            });

            $(dropdownParent).mouseleave(function() {
				$(dropdownList + " a", this).attr("tabindex", "-1");
				$(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
            }).focusout(function() {
                var thisDropdown = this;

                setTimeout(function () {
                    if(!$(thisDropdown).find(":focus").length) {
                        $(dropdownSelector, thisDropdown).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    }
                }, 500);
            });
        },

		"Header": function() {
            // ADD LOGO
            var logoSrc = jQuery.trim("/cms/lib/CA01001466/Centricity/Template/GlobalAssets/images///logos/logo.png");
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
            var logoContainer = (("true" == "false" && "true" == "false") || ("West Contra Costa" == '' && "Unified School District" == '')) ? $('#gb-sitename') : $('#gb-logo-outer') ;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
            	if(!true) {
                	logoContainer.append("
").removeClass("hidden");
                } else {
					logoContainer.append("").removeClass("hidden");
                }
			} else {
            	if(!true) {
                	logoContainer.append("
").removeClass("hidden");
                } else {
					logoContainer.append("").removeClass("hidden");
                }
			}

            if(("true" == "false" && "true" == "false") || ("West Contra Costa" == '' && "Unified School District" == '')){
                $('#gb-logo-outer').remove();
				logoContainer
                	.attr('data-show-desktop-logo', 'true')
                    .attr('data-show-mobile-logo', 'true')
                    .attr('id', 'gb-logo-outer')
                    .addClass('logo-only');

				$('#gb-sitename-outer').addClass('logo-only');
                CreativeTemplate.LogoOnly = true;

            }

            if("West Contra Costa" == '') $('.sitename-one').remove();
            if("Unified School District" == '') $('.sitename-tagline').remove();


            logoContainer.removeClass('hidden').show();

            CreativeTemplate.GlobalIconDialog();
        },

        "ChannelBar": function() {
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

			$("#navc-HP.sw-channel-item").attr('aria-label', 'Home channel');
			$("#navc-HP.sw-channel-item span").html('');

            $(".sw-channel-item").each(function(){
                if($(".sw-channel-dropdown li", this).length == 0) {
                    $(this).addClass("no-dropdown");
                }
            });
        },

        "StickyChannelBar": function() {
			if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var navOffSet = $('#gb-channel-list-outer').outerHeight() - 15;
                        if ($(window).scrollTop() <= navOffSet) {
                            if(this.ChannelBarIsSticky) {
                                $("#sticky-header-outer").removeAttr("style").removeClass("sticky");

                                this.ChannelBarIsSticky = false;
                            }
                        } else {
                            $("#sticky-header-outer").css("height", $("#gb-channel-list-outer").height());

                            if(!this.ChannelBarIsSticky) {
                                $("#sticky-header-outer").addClass("sticky");

                                this.ChannelBarIsSticky = true;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("#gb-channel-list-outer").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.ChannelBarIsSticky) {
                            $("#sticky-header-outer").removeAttr("style").removeClass("sticky");

                            this.ChannelBarIsSticky = false;
                        }

                        $("#sw-maincontent").removeAttr("style");
                    break;
                }
            }
        },

        "Body": function() {
        	// FOR SCOPE
            var _this = this;

			if('Grey' == 'Black'){
            	$('body').addClass('cs-text-color-black');
            }

            // ADD CLASS TO NAV IF DASHBOARD IS ACTIVE
            if($("#dashboard-sidebar").length) {
                $("nav").addClass("dashboard-active");
            }

            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $(".ui-widget.app .ui-widget-detail img")
                .not($(".ui-widget.app.multimedia-gallery .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            //move controls and more links
            $(".ui-widget.app").each(function(appIndex, app){
            	//if we have an applicable button
            	if($('.more-link', app).length > 0 || $('.view-calendar-link', app).length > 0 || $('.app-level-social-rss', app).length > 0 || $('.app-level-social-follow', app).length > 0 || $('.app-level-social-contribute', app).length > 0){
                	//add a container to the header to hold the buttons and class the header to manage these new additions
                	$('.ui-widget-header', app)
                    	.addClass('flex-cont ai-c jc-sb')
                        .append('
');

					//if we have any more links add them
                    if($('.more-link', app).length > 0){
                    	$('.cs-header-control-container', app).append($('.more-link', app));
                    }else if($('.view-calendar-link', app).length > 0){
                    	$('.cs-header-control-container', app).append($('.view-calendar-link', app));
                    }

                    if($('.app-level-social-rss', app).length > 0){
                    	$('.cs-header-control-container', app).append($('.app-level-social-rss a', app).html(''));
                        $('.app-level-social-rss', app).remove();
                    }

                    if($('.app-level-social-follow', app).length > 0){
                    	$('.cs-header-control-container', app).append($('.app-level-social-follow', app));
                    }

                    if($('.app-level-social-contribute', app).length > 0){
                    	$('.cs-header-control-container', app).append($('.app-level-social-contribute', app));
                    }
                }
            });

            $('#hp-content div[data-cs-type="region-container"]').each(function(sectionIndex, section){
            	if( $('.region', section).children().length <= 0 ){
                	if( $(section).hasClass('hp-column') ){
                    	$('#hp-content .hp-row.four .gb-section').addClass('one-column');
                    }

                	$(section).parent().empty();
                }
            });

            if( '/cms/lib/CA01001466/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg'.indexOf('default-man') >= 0 || '/cms/lib/CA01001466/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg' == '' ){
                $('#hp-content .hp-row.four').addClass('default-background');
            }

            if( '/cms/lib/CA01001466/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg'.indexOf('default-man') >= 0 || '/cms/lib/CA01001466/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg' == '' ){
                $('#hp-content .article-sliders .upcomingevents').addClass('default-background');
            }

            $('#hp-content .hp-row').each(function(rowIndex, row){
            	if( $(row).children().length <= 0 ){
                	$(row).hide();
                }
            });

            if( $('#hp-content .hp-row.four .gb-section').children().length <= 0 ){
            	$('#hp-content .hp-row.four').hide();
            }


            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first").html("Home");
        },

        "Footer": function() {
