			];

            footerLinkHtml = '';

			$.each(footerLinks, function(linkIndex, link){
            	if(link.url != "" && link.label != ""){
                	footerLinkHtml += '
' + link.label + '\n';
                }
            });

            $('#extra-footer-links').prepend(footerLinkHtml);

            $('.gb-footer-contact.email.false').remove();

            // MOVE Bb FOOTER STUFF
            $(".gb-schoolwires-footer.logo").html($("#sw-footer-logo").html());
            var schoolwiresLinks = '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(0)").html().replace("|", "")) + '';
            schoolwiresLinks += '
