        "ArticleSliders": function() {
			$(".article-sliders .ui-widget.app.headlines").csArticleSlider({
                "viewerNum": [3, 2, 2, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "transitionSpeed": .5 // SECONDS
            });

            $(".article-sliders .ui-widget.app.upcomingevents").csArticleSlider({
                "viewerNum": [5, 3, 2, 2, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "transitionSpeed": .5 // SECONDS
            });

            $(".article-sliders .ui-widget.app.announcements").csArticleSlider({
                "viewerNum": [3, 2, 2, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "transitionSpeed": .5 // SECONDS
            });

            //move controls and more links
            $(".article-sliders .ui-widget.app").each(function(appIndex, app){
            	//if we have an applicable button
            	if($('.cs-article-slider-control', app).length > 0 || $('.more-link', app).length > 0 || $('.view-calendar-link', app).length > 0){
                	//add a container to the header to hold the buttons and class the header to manage these new additions
                	if($('.cs-header-control-container', app).length <= 0){
                        $('.ui-widget-header', app)
                            .addClass('flex-cont ai-c jc-sb')
                            .append('
');
                    }

                    //if we have slider controls add them first
                    if($('.cs-article-slider-control', app).length > 0){
                    	$('.cs-header-control-container', app)
                        	.prepend($('.cs-article-slider-control.next', app).html('NEXT'))
                        	.prepend($('.cs-article-slider-control.back', app).html('PREV'));
                    }
                }
            });

            $(".article-sliders .ui-widget.app.upcomingevents").each(function(appIndex, app){
            	$('.ui-articles > li', app).each(function(liIndex, li){
                	if( $('.ui-article-description', li).length > 2 ){
                    	$('.ui-article-description:gt(1)', li)
                        	.attr('aria-hidden', 'true')
                            .hide(0);

                        $('.ui-article-description:gt(1) a', li).attr('tabindex', -1);

                        $('.upcoming-column.right', li).append('More ');
                    }
                });
            });

            $('.article-sliders .cs-upcoming-events-more-trigger').click(function(e){
            	var containerParent = $(this).parent();

                if( $(this).attr('aria-expanded') == 'false' ){
                	$(this).attr('aria-expanded', 'true');

                    $('.ui-article-description[aria-hidden="true"] a', containerParent).attr('tabindex', 0);

                	$('.ui-article-description[aria-hidden="true"]', containerParent)
                        .attr('aria-hidden', 'false')
                        .show(300);
                    $('.ui-article-description:eq(2) a', containerParent).focus();
                }else{
                	$(this).attr('aria-expanded', 'false');

                    $('.ui-article-description[aria-hidden="true"] a', containerParent).attr('tabindex', -1);

                	$('.ui-article-description[aria-hidden="false"]', containerParent)
                        .attr('aria-hidden', 'true')
                        .hide(300);	
                }
            });
        },

        "Search": function() {
        	// FOR SCOPE
            var _this = this;
			var defSearchText = "I'm looking for...";

			$("#gb-search-form").submit(function(e){
				e.preventDefault();
				if($("#gb-search-input").val() != defSearchText) {
					window.location.href = "/site/Default.aspx?PageType=6&SiteID=1&SearchString=" + $("#gb-search-input").val();
                }
			});

			$("#gb-search-input")
				.attr('value', defSearchText)
                .focus(function() {
                    if($(this).val() == defSearchText) {
                        $(this).val("");
                    }
                }).blur(function() {
                    if($(this).val() == "") {
                        $(this).val(defSearchText);
                    }
                });
        },

        "A11yClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown") {
                if(event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter) {
                    return true;
                }
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

        "GlobalIconDialog": function() {
            $('#gb-nav-dialog-trigger').click(function(e){
            	var dialog = $('#' + $(this).attr('aria-controls'));
                dialog
                  .attr('aria-hidden', 'false')
                  .fadeIn(300);

                $(this).fadeOut(300);

                $('a, input, button', dialog).attr('tabindex', 0);

                setTimeout(function(e){
                	$("#gb-search-input").focus();
                }, 300);
            });

            $('#gb-nav-dialog-nested-trigger').click(function(e){
            	e.stopPropagation();
            	var dialog = $('#' + $(this).attr('aria-controls'));
            	dialog
                    .attr('aria-hidden', 'true')
                    .fadeOut(300);

				$('a, input, button', dialog).attr('tabindex', -1);

                $('#gb-nav-dialog-trigger').fadeIn(300).focus();
            })
            .keydown(function(e){
            	if( e.shiftKey && e.keyCode == CreativeTemplate.KeyCodes.tab ){
                	e.preventDefault();
                    $('#gb-nav-dialog .cs-dialog-panel-last-link').focus();
                }
            });

            //if the dialog gets clicked, with the exception of the dialog-panel, we'll close the menu.
            $("#gb-nav-dialog").click(function(e){
            	$('#gb-nav-dialog-nested-trigger').trigger('click');
            });

            //prevent clicking of the children from propogating to the parent
            $("#gb-nav-dialog .cs-dialog-panel").click(function(e) {
        		e.stopPropagation();
 			});

            var lastTabbableElement = '';
            if( $('#gb-nav-dialog .cs-global-icon').length > 0 ){
            	lastTabbableElement = $('#gb-nav-dialog .cs-global-icon').last();
            }else{
            	lastTabbableElement = $('#gb-nav-dialog #gb-search-button');
            }

            lastTabbableElement
            .addClass('cs-dialog-panel-last-link')
            .keydown(function(e){
                if( CreativeTemplate.KeyCodes.tab == e.keyCode && !e.shiftKey ){
                    e.preventDefault();
                    $('#gb-nav-dialog-nested-trigger').focus();
                }
            });

            var getWindowHeight = $(window).height();
            $(".cs-dialog-panel").css({
            	"max-height": getWindowHeight + "px",
                "overflow": "auto"
            });

            $(window).resize(function() {
            	var getWindowHeight = $(window).height();
                $(".cs-dialog-panel").css({
                    "max-height": getWindowHeight + "px",
                    "overflow": "auto"
                });
            });
        },

        "BuildStylesheet": function() {
        	var dynStyleSheet = document.createElement('style');

            if(dynStyleSheet) {
                dynStyleSheet.setAttribute('type', 'text/css');
                var head = document.getElementsByTagName('head')[0];

                if(head) {
                    head.insertBefore(dynStyleSheet, head.childNodes[0]);
                }

                var rules = document.createTextNode(this.DynamicStyles);

                if(dynStyleSheet.styleSheet){ // IE
                    dynStyleSheet.styleSheet.cssText = this.DynamicStyles;
                } else {
                    dynStyleSheet.appendChild(rules);
                }
            }
        },

        "HexToRgb": function(hex){
        	var r = parseInt((this.CutHex(hex)).substring(0,2),16);
	        var g = parseInt((this.CutHex(hex)).substring(2,4),16);
	        var b = parseInt((this.CutHex(hex)).substring(4,6),16);
	        return r+', '+g+', '+b;
        },

        "CutHex": function(hex){
        	return (hex.charAt(0)=="#") ? hex.substring(1,7):hex;
        },

        "Shortcuts": function() {
            // EDIT THESE TWO VARS
        	var columnNums = [3, 1, 1, 1, 1]; // [960, 768, 640, 480, 320]

            // RETURN BREAKPOINT INDEX
            var bp = function() {
                switch(CreativeTemplate.GetBreakPoint()) {
                    case "desktop": return 0; break;
                    case "768": return 1; break;
                    case "640": return 2; break;
                    case "480": return 3; break;
                    case "320": return 4; break;
                }
            }

            // SET COLUMN NUM AND OTHER VARS
            var columnNum = columnNums[bp()];
            var endRange;

            $('.hp .article-sliders .siteshortcuts').each(function() {
            	// RETURN THE LI'S TO THE ORIGINAL UL
                $(".ui-widget-detail > ul.site-shortcuts", this).append($(".site-shortcuts-column > li", this));

                // REMOVE COLUMN CONTAINER FOR REBUILD
                $(".site-shortcuts-columns", this).remove();

                // GET SHORTCUT NUM
            	var shortcutNum = $(".ui-widget-detail > ul.site-shortcuts > li", this).length;

                // ADD COLUMN CONTAINER
                $(".ui-widget-detail", this).prepend('
');

                // LOOP TO BUILD COLUMNS
                for(var i = 0; i < columnNum; i++) {
                	// KEEP FROM ADDING EMPTY UL'S TO THE DOM
                    if(i < shortcutNum) {
                		// IF shortcutNum / columnNum REMAINDER IS BETWEEN .0 AND .5 AND THIS IS THE FIRST LOOP ITERATION
                    	// WE'LL ADD 1 TO THE END RANGE SO THAT THE EXTRA LINK GOES INTO THE FIRST COLUMN
                        if((shortcutNum / columnNum) % 1 > 0.0 && (shortcutNum / columnNum) % 1 < 0.5 && i == 0) {
                            endRange = Math.round(shortcutNum / columnNum) + 1;
                        } else {
                            endRange = Math.round(shortcutNum / columnNum)
                        }

                        // ADD THE COLUMN UL
                        $(".site-shortcuts-columns", this).append('');

                        // MOVE THE RANGE OF LI'S TO THE COLUMN UL
                        for(var r = 1; r <= endRange; r++) {
                                if($(".ui-widget-detail > ul.site-shortcuts > li:first-child", this).length) {
                                        $(".site-shortcuts-column.column-" + (i + 1), this).append($(".ui-widget-detail > ul.site-shortcuts > li:first-child", this));
                                }
                        }
                	}
                }

                // HIDE THE ORIGINAL UL
                $(".ui-widget-detail > ul.site-shortcuts", this).hide();

                $('.site-shortcuts-columns', this).addClass('flex-cont');
            });
        }
    };

