div.script:
function googleTranslateElementInit2() {
  new google.translate.TranslateElement({pageLanguage: 'en',autoDisplay: false}, 'google_translate_element2');
function googleTranslateAccessibility() {
  jQuery('#google_translate_element2 select').each(function (i) {jQuery(this).attr('title', 'Select language');});
  jQuery('#goog-gt-tt img, #google_translate_element2 img').each(function (i) {jQuery(this).attr('alt', 'null');});
}}
