body.script:
var domainsToReplace = [
	'http://www.psdschools.org',
	'https://www.psdschools.org'
];
jQuery(document).ready(function() {
    jQuery('a').each(function(index, item) {
        if (typeof jQuery(item).attr('href') !== 'undefined') {
			for (var i = 0; i < domainsToReplace.length; i++) {
                if (jQuery(item).attr('href').indexOf(domainsToReplace[i]) === 0) {
                	jQuery(item).attr('href', jQuery(item).attr('href').replace(domainsToReplace[i], ''));
                }
            }
        }
    });
});

