imageRotatorJQuery = jQuery.noConflict();
imageRotatorJQuery(window).load(function() {
	var container = document.getElementById("ir5774");
	var rotator = document.getElementById("ir5774-ir5774-slider"); 
	if(container) {
		if(container.className!="ir5774-duplicateRotatorDiv") {
			var sliderNode = null;
			while(container.hasChildNodes()) {
				var lastChild = container.lastChild;
				if(lastChild.className=="ir5774-duplicateRotatorDiv") {
					sliderNode = lastChild.cloneNode(true);
					sliderNode.setAttribute("id", "ir5774-sliderContainer");
					sliderNode.style.display = "block";
				}
				container.removeChild(lastChild);
			}

			if(sliderNode) {
				container.appendChild(sliderNode);
			} else if(rotator) {
				container.appendChild(rotator);
			}
		}
		container.style.display = "block";
	}

	var _transition = 'random';
	if(/MSIE (\d+\.\d+);/.test(navigator.userAgent)) {
		var IEVersion = new Number(RegExp.$1);
		if(IEVersion<9) {
			_transition = 'fade';
		}
	}

	_transition = 'fade';


    curNivo = imageRotatorJQuery('#ir5774-ir5774-slider').nivoSlider({
        transition: _transition,
        slices: Math.ceil("30"),
        boxCols: Math.ceil("20"),
        boxRows: Math.ceil("6"),
        animationSpeed: 500,
        rotateTime: 5000,
        showNavigation: false,
        beforeChange: function() { },
        afterChange: function() { },
        slideshowEnd: function() { },
        lastSlide: function() {

        },
        afterLoad: function() { },
        images: [

			{ 
				src: "//www.district70.org/rotating_images/5774/5774_2_1200_0_0.jpg",
				caption: {
					"text": "",
					"styling": "",
					"show": false
				},
				loaded: false
			}

        ],
        preloadQueueLength: 2
    });
});

function resizeSlider_ir5774() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("1100");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("ir5774-ir5774-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("ir5774-ir5774-slider").style.height = Math.max((parseFloat("250")/parseFloat("1200") * containerWidth), parseFloat("250")) + "px";
	document.getElementById("ir5774-ir5774-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

if(window.addEventListener) {
	window.addEventListener("resize", function() { resizeSlider_ir5774(); }, false);
} else if(window.attachEvent) {
	window.attachEvent("onresize", function() { resizeSlider_ir5774(); });
}
resizeSlider_ir5774();

