var style5264 = "";
    $(document).ready(function () {

        createTableView5264(5264, "True", "True");
        
        windowresize5264();
    });

	$(window).resize( function () {
      windowresize5264();
    });

	function isTable5264Responsive() {
    	var contentBreakPoint5264 = 600;
        
        if ('500' != '') {
        	contentBreakPoint5264 = '500';
            
        }
       
    	var screenWidth = (window.innerWidth > 0) ? window.innerWidth : screen.width;
		var contentWidth5264 = $("#tableApp5264").width();
        // Jason S - changed to trigger responsive for any screen size under 1024px 
        var deviceBool5264 = (screenWidth < 1024);
		
        if ( deviceBool5264 || contentWidth5264 < contentBreakPoint5264 ) {
        	return true;
        } else {
        	return false;
        }
    }
    
	function windowresize5264() {
		// trigger resposive if < 1024 screen size or content size below 600 - used for table app in multiple column layouts
        if ( isTable5264Responsive() ) {
            $("#tableApp5264").addClass("triggerResponsive");
            addResponsiveStyle5264();
        } else {
            $("#tableApp5264").removeClass("triggerResponsive");
            $("#tableapp-responsivestyle-tableApp5264").remove();
        } 
    }
    
    function addResponsiveStyle5264() {
        if ($("#tableapp-responsivestyle-tableApp5264").length == 0 && style5264 != "") {
    		// Jason S. - changed max width to 1024 to prevent losing headings when responsiveness is triggered SCB-7487
        	$('head').append("");
        }
    }

    function createTableView5264(id, hasHeaderRow, hasHeaderCol) {
		
        if ($("#hidData" + id).val() != "") {
            var tableId = "tableApp" + id
            var data = JSON.parse($("#hidData" + id).val());
            var classNames = JSON.parse($("#hidClassNames" + id).val());

            var headeHtml = "";
            var bodyHtml = "";
            var height = "auto";
            var width = "auto";
            var rowTitle = [];
            var columnTitle = [];
            style5264 = " caption {display: block;}.tableAppHead {position: absolute;top: -9999px;left: -9999px;}";
            $.each(data, function (row, rowItem) {
                if (hasHeaderRow == "True" && row == 0) {
                    headeHtml += "
";
                    $.each(rowItem, function (column, columnItem) {
                        var className = "";
                        if (classNames.length > row && classNames[row].length > column) {
                            className = classNames[row][column];
                        }
                        headeHtml += "
" + (columnItem == null || columnItem == "" ? " " : columnItem) + "";
                        rowTitle.push(columnItem == null ? " " : columnItem);
                    });
                    headeHtml += "";
                } else {
                    bodyHtml += "
";
                    $.each(rowItem, function (column, columnItem) {
                        var className = "";
                        if (classNames.length > row && classNames[row].length > column) {
                            className = classNames[row][column];
                        }
                        if (hasHeaderCol == "True" && column == 0) {
                            bodyHtml += "
" + (columnItem == null || columnItem == "" ? " " : columnItem) + "";
                            columnTitle.push(columnItem == null ? " " : columnItem);
                        } else {
                            bodyHtml += "
" + (columnItem == null || columnItem == "" ? " " : columnItem) + "";
                        }
                    });
                    bodyHtml += "";
                }
            });
            $("#" + tableId + " thead ").html(headeHtml);
            $("#" + tableId + " tbody ").html(bodyHtml);
            $.each(rowTitle, function (index, item) {
                if (hasHeaderCol == "True" && hasHeaderRow == "True") {
                    if (index != 0) {
                        style5264 += "#" + tableId + " td:nth-of-type(" + index + "):before { content: '" + $("
"+item+"").text() + "'; }";
                    }
                } else {

                    style5264 += "#" + tableId + " td:nth-of-type(" + (index + 1) + "):before { content: '" + $("
" + item + "").text() + "'; }";

                }


            });
            if (hasHeaderRow == "False" && hasHeaderCol == "True") {
                $.each(columnTitle, function (index, item) {
                    $("#" + tableId + " tbody tr").eq(index).prepend($("" + $("
" + item + "").text() + ""));
                });
            }
            
            if (isTable5264Responsive()) {
            	addResponsiveStyle5264();
            }
            
        }
    }

