}

function ViewEventClick(sender, theEvent){   
   var url = '/cms/One.aspx?portalId=319989&pageId=5639909&contextId.439526=5639911&parentId.439526=7611422&returnTo=7611422&objectId.439526=' + theEvent.EventId + '&currentDate='+sender.get_currentDate().format("yyyy-MM-dd")+'&view='+sender.get_viewModeName() + (theEvent.Reference == '' ? '' : '&ref=' + theEvent.Reference);   
   window.location = url;
}

