body.script:
$(".slider-button").click(function (event) {
    event.stopPropagation();
    let slider = $(".slider");
    let sidePanel = $(".side-panel");
    if (slider.hasClass("slider-visible")) {
        slider.removeClass("slider-visible");
        sidePanel.removeClass("side-panel-visible");
    } else {
        slider.addClass("slider-visible");
        sidePanel.addClass("side-panel-visible");
    }
});
$(".close-sign").click(function (event) {
    event.stopPropagation();
    $(".slider").removeClass("slider-visible");
    $(".side-panel").removeClass("side-panel-visible");
});

                    (function () {
                        var $newTabLinks = $('a[target="_blank"]');
                        var $newTabHiddenElement = $(' Opens in a new browser tab ');

                        $newTabLinks.attr('rel', 'noopener');
                        $newTabLinks.each(function () {
                            var $this = $(this);
                            if ($this.children('span.new-tab-link-info').length === 0) {
                                $this.append($newTabHiddenElement.clone());
                            }
                        });
                    })();
