body.script:
(function(d, s, id) {
		  var js, fjs = d.getElementsByTagName(s)[0];
		  if (d.getElementById(id)) return;
		  js = d.createElement(s); js.id = id;
		  js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.10&appId=1528452973878586";
		  fjs.parentNode.insertBefore(js, fjs);
		}(document, 'script', 'facebook-jssdk'));

div.script:
	jQuery(function($) {
		$(".hp-tiles").matchHeight();
		$(".border-frame").matchHeight();
		$(".apply-tile").matchHeight({target: $('.main-content')});
		$(".eventName").matchHeight({target: $('.eventDate')});
	});
	$(function() {
	  $('a[href*="#"]:not([href="#"])').click(function() {
	    if (location.pathname.replace(/^\//,'') == this.pathname.replace(/^\//,'') && location.hostname == this.hostname) {
	      var target = $(this.hash);
	      target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
	      if (target.length) {
        	$('html, body').animate({
          		scrollTop: target.offset().top-175
          	}, 1000);
	        return false;
	      }
	    }
	  });
	})
